      out.write("        // Thêm hoặc sửa sản phẩm vào hợp đồng\r\n");
      out.write("        function addProductToContract() {\r\n");
      out.write("            var productId = $('#newProductId').val();\r\n");
      out.write("            var description = $('#newDescription').val();\r\n");
      out.write("            var quantity = $('#newQuantity').val();\r\n");
      out.write("            var unitPrice = $('#newUnitPrice').val().replace(/[.,\\s]/g, ''); // Bỏ dấu phẩy, chấm, khoảng trắng\r\n");
      out.write("            var warrantyMonths = $('#newWarrantyMonths').val();\r\n");
      out.write("            var notes = $('#newNotes').val();\r\n");
      out.write("            var editingIndex = $('#editingProductIndex').val(); // Nếu có thì là đang sửa\r\n");
      out.write("\r\n");
      out.write("            if (!productId || !quantity || !unitPrice) {\r\n");
      out.write("                showAlert('Vui lòng chọn sản phẩm và nhập số lượng', 'warning');\r\n");
      out.write("                return;\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            // Kiểm tra tồn kho\r\n");
      out.write("            var selectedOption = $('#newProductId').find('option:selected');\r\n");
      out.write("            var availableQuantity = parseFloat(selectedOption.data('quantity')) || 0;\r\n");
      out.write("            var requestedQuantity = parseFloat(quantity);\r\n");
      out.write("            var editingIndexNum = editingIndex !== '' ? parseInt(editingIndex) : -1;\r\n");
      out.write("            \r\n");
      out.write("            // Tính tổng số lượng đã thêm vào hợp đồng (trừ sản phẩm đang sửa nếu có)\r\n");
      out.write("            var totalQuantityInContract = 0;\r\n");
      out.write("            contractProducts.forEach(function(p, index) {\r\n");
      out.write("                if (p.productId == productId && index !== editingIndexNum) {\r\n");
      out.write("                    totalQuantityInContract += parseFloat(p.quantity) || 0;\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Tính số lượng khả dụng thực tế = tồn kho - số lượng đã thêm vào hợp đồng\r\n");
      out.write("            var actuallyAvailable = availableQuantity - totalQuantityInContract;\r\n");
      out.write("            \r\n");
      out.write("            // Cảnh báo nếu không đủ tồn kho (nhưng vẫn cho phép thêm)\r\n");
      out.write("            if (requestedQuantity > actuallyAvailable) {\r\n");
      out.write("                var productName = selectedOption.text().split(' - ')[1] || selectedOption.text();\r\n");
      out.write("                if (productName.indexOf('(') > 0) {\r\n");
      out.write("                    productName = productName.substring(0, productName.indexOf('(')).trim();\r\n");
      out.write("                }\r\n");
      out.write("                showAlert('Cảnh báo: Sản phẩm \"' + productName + '\" hiện tại trong kho tồn không đủ số lượng. ' + \r\n");
      out.write("                         'Tồn kho hiện có: ' + actuallyAvailable.toLocaleString() + \r\n");
      out.write("                         ', yêu cầu: ' + requestedQuantity.toLocaleString() + \r\n");
      out.write("                         '. Vui lòng báo kho nhập thêm sản phẩm và điều chỉnh lại điều khoản hợp đồng cho phù hợp hơn.', 'warning');\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            // Tạo object sản phẩm\r\n");
      out.write("            var product = {\r\n");
      out.write("                productId: parseInt(productId),\r\n");
      out.write("                description: description,\r\n");
      out.write("                quantity: parseFloat(quantity),\r\n");
      out.write("                unitPrice: parseFloat(unitPrice),\r\n");
      out.write("                warrantyMonths: warrantyMonths ? parseInt(warrantyMonths) : null,\r\n");
      out.write("                notes: notes\r\n");
      out.write("            };\r\n");
      out.write("\r\n");
      out.write("            // Nếu đang sửa thì cập nhật, không thì thêm mới\r\n");
      out.write("            if (editingIndex !== '') {\r\n");
      out.write("                contractProducts[parseInt(editingIndex)] = product; // Sửa\r\n");
      out.write("            } else {\r\n");
      out.write("                contractProducts.push(product); // Thêm mới\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            renderContractProducts(); // Vẽ lại bảng\r\n");
