      out.write("        function updateDeletedPaginationInfo() {\r\n");
      out.write("            var pageSize = parseInt($('#deletedPageSize').val());\r\n");
      out.write("            var start = (deletedContractsCurrentPage - 1) * pageSize + 1;\r\n");
      out.write("            var end = Math.min(deletedContractsCurrentPage * pageSize, deletedContractsTotalRecords);\r\n");
      out.write("            \r\n");
      out.write("            if (deletedContractsTotalRecords === 0) {\r\n");
      out.write("                start = 0;\r\n");
      out.write("                end = 0;\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("            $('#deletedPaginationInfo').text('Hiển thị ' + start + ' - ' + end + ' của ' + deletedContractsTotalRecords + ' bản ghi');\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
      out.write("        function renderDeletedPagination() {\r\n");
      out.write("            var pagination = $('#deletedPagination');\r\n");
      out.write("            pagination.empty();\r\n");
      out.write("            \r\n");
      out.write("            // Luôn hiển thị nút Previous\r\n");
      out.write("            var prevDisabled = deletedContractsCurrentPage <= 1 ? 'disabled' : '';\r\n");
      out.write("            pagination.append('<li class=\"' + prevDisabled + '\"><a href=\"#\" onclick=\"loadDeletedContracts(' + (deletedContractsCurrentPage - 1) + '); return false;\">&laquo;</a></li>');\r\n");
      out.write("            \r\n");
      out.write("            // Nếu chỉ có 1 trang, vẫn hiển thị nút trang đó\r\n");
      out.write("            if (deletedContractsTotalPages <= 1) {\r\n");
      out.write("                pagination.append('<li class=\"active\"><a href=\"#\" onclick=\"loadDeletedContracts(1); return false;\">1</a></li>');\r\n");
      out.write("            } else {\r\n");
      out.write("                // Các nút trang\r\n");
      out.write("                var startPage = Math.max(1, deletedContractsCurrentPage - 2);\r\n");
      out.write("                var endPage = Math.min(deletedContractsTotalPages, deletedContractsCurrentPage + 2);\r\n");
      out.write("                \r\n");
      out.write("                if (startPage > 1) {\r\n");
      out.write("                    pagination.append('<li><a href=\"#\" onclick=\"loadDeletedContracts(1); return false;\">1</a></li>');\r\n");
      out.write("                    if (startPage > 2) {\r\n");
      out.write("                        pagination.append('<li class=\"disabled\"><span>...</span></li>');\r\n");
      out.write("                    }\r\n");
      out.write("                }\r\n");
      out.write("                \r\n");
      out.write("                for (var i = startPage; i <= endPage; i++) {\r\n");
      out.write("                    var active = i === deletedContractsCurrentPage ? 'active' : '';\r\n");
      out.write("                    pagination.append('<li class=\"' + active + '\"><a href=\"#\" onclick=\"loadDeletedContracts(' + i + '); return false;\">' + i + '</a></li>');\r\n");
      out.write("                }\r\n");
      out.write("                \r\n");
      out.write("                if (endPage < deletedContractsTotalPages) {\r\n");
      out.write("                    if (endPage < deletedContractsTotalPages - 1) {\r\n");
      out.write("                        pagination.append('<li class=\"disabled\"><span>...</span></li>');\r\n");
      out.write("                    }\r\n");
      out.write("                    pagination.append('<li><a href=\"#\" onclick=\"loadDeletedContracts(' + deletedContractsTotalPages + '); return false;\">' + deletedContractsTotalPages + '</a></li>');\r\n");
      out.write("                }\r\n");
      out.write("            }\r\n");
