      out.write("\r\n");
      out.write("        function restoreContract(id) {\r\n");
      out.write("            if (!confirm('Bạn có chắc chắn muốn khôi phục hợp đồng này? Hợp đồng sẽ được khôi phục về trạng thái \"Bản nháp\".')) {\r\n");
