      out.write("            $.post('api/contracts', { action: 'permanent_delete', id: id }, function(resp) {\r\n");
      out.write("                if (resp.success) {\r\n");
      out.write("                    showAlert(resp.message, 'success', 8000); // Hiển thị 8 giây cho thông báo xóa vĩnh viễn\r\n");
      out.write("                    loadDeletedContracts(); // Reload danh sách thùng rác\r\n");
      out.write("                    // Reload trang chính sau khi xóa vĩnh viễn\r\n");
