      out.write("\r\n");
      out.write("        function showAlert(message, type, timeout) {\r\n");
      out.write("            // Kiểm tra xem có đang trong modal không\r\n");
      out.write("            if ($('#contractModal').hasClass('in') || $('#contractModal').is(':visible')) {\r\n");
      out.write("                // Hiển thị trong modal\r\n");
      out.write("                showModalAlert(message, type, timeout);\r\n");
      out.write("            } else {\r\n");
      out.write("                // Hiển thị ở trang chính\r\n");
      out.write("                var alertClass = 'alert-' + type;\r\n");
      out.write("                var html = '<div class=\"alert ' + alertClass + ' alert-dismissible\" role=\"alert\">' +\r\n");
      out.write("                           '<button type=\"button\" class=\"close\" data-dismiss=\"alert\" aria-label=\"Close\">' +\r\n");
      out.write("                           '<span aria-hidden=\"true\">&times;</span></button>' + message + '</div>';\r\n");
      out.write("                $('.content').prepend(html);\r\n");
      out.write("                // Mặc định  giây, nhưng có thể tùy chỉnh\r\n");
      out.write("                var delay = timeout || 3000;\r\n");
      out.write("                setTimeout(function(){ $('.alert').fadeOut(400, function(){ $(this).remove(); }); }, delay);\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function showModalAlert(message, type, timeout) {\r\n");
      out.write("            var alertClass = 'alert-' + type;\r\n");
      out.write("            var iconClass = type === 'success' ? 'fa-check-circle' : \r\n");
