      out.write("                           type === 'info' ? 'fa-info-circle' : 'fa-exclamation-triangle';\r\n");
      out.write("            \r\n");
      out.write("            $('#contractErrorAlert')\r\n");
      out.write("                .removeClass('alert-success alert-warning alert-info alert-danger')\r\n");
      out.write("                .addClass(alertClass)\r\n");
      out.write("                .show();\r\n");
      out.write("            \r\n");
      out.write("            $('#contractErrorText').html('<i class=\"fa ' + iconClass + '\"></i> ' + message);\r\n");
      out.write("            \r\n");
      out.write("            // Mặc định: success 2 giây, các loại khác 3 giây. Có thể tùy chỉnh qua tham số timeout\r\n");
      out.write("            var hideDelay = timeout || (type === 'success' ? 2000 : 3000);\r\n");
      out.write("            setTimeout(function() {\r\n");
