      out.write("\r\n");
      out.write("        function formatDateInput(value) {\r\n");
      out.write("            if (!value) return '';\r\n");
      out.write("            try {\r\n");
      out.write("                var d = new Date(value);\r\n");
      out.write("                if (isNaN(d.getTime())) return '';\r\n");
      out.write("                var m = (d.getMonth() + 1).toString().padStart(2, '0');\r\n");
      out.write("                var day = d.getDate().toString().padStart(2, '0');\r\n");
      out.write("                return d.getFullYear() + '-' + m + '-' + day;\r\n");
      out.write("            } catch (e) { return ''; }\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
      out.write("        // Hàm format thời gian theo múi giờ Việt Nam\r\n");
      out.write("        function formatVietnamTime(dateString) {\r\n");
      out.write("            if (!dateString || dateString === '-') return '-';\r\n");
      out.write("            try {\r\n");
      out.write("                var date = new Date(dateString);\r\n");
      out.write("                if (isNaN(date.getTime())) return dateString;\r\n");
      out.write("                \r\n");
      out.write("                // Database lưu theo UTC, cần trừ đi 7 tiếng để có múi giờ Việt Nam\r\n");
      out.write("                var vietnamTime = new Date(date.getTime() - (7 * 60 * 60 * 1000));\r\n");
      out.write("                return vietnamTime.toLocaleString('vi-VN', {\r\n");
      out.write("                    year: 'numeric',\r\n");
      out.write("                    month: '2-digit',\r\n");
      out.write("                    day: '2-digit',\r\n");
      out.write("                    hour: '2-digit',\r\n");
      out.write("                    minute: '2-digit',\r\n");
      out.write("                    second: '2-digit'\r\n");
      out.write("                });\r\n");
      out.write("            } catch (e) {\r\n");
      out.write("                console.error('Error formatting Vietnam time:', e);\r\n");
      out.write("                return dateString;\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
