      out.write("\n");
      out.write("    <script type=\"text/javascript\">\n");
      out.write("        var customersTable;\n");
      out.write("        var currentEditingCustomer = null;\n");
      out.write("\n");
      out.write("            $(document).ready(function () {\n");
      out.write("            customersTable = $('#customersTable').DataTable({\n");
      out.write("                \"language\": { \"url\": \"//cdn.datatables.net/plug-ins/1.10.25/i18n/Vietnamese.json\" },\n");
      out.write("                    \"processing\": false,\n");
      out.write("                    \"serverSide\": false,\n");
      out.write("                    \"paging\": true,\n");
      out.write("                    \"searching\": false,\n");
      out.write("                    \"dom\": 'lrtip',\n");
      out.write("                    \"ordering\": true,\n");
      out.write("                    \"info\": true,\n");
      out.write("                    \"autoWidth\": false,\n");
      out.write("                    \"responsive\": true,\n");
      out.write("                \"order\": [[0, \"desc\"]],\n");
      out.write("                \"columnDefs\": [{ \"targets\": [10], \"orderable\": false, \"searchable\": false }]\n");
      out.write("            });\n");
      out.write("\n");
      out.write("                // Hiển thị tóm tắt lựa chọn hiện tại (đọc từ tham số GET đã bind sẵn vào selected)\n");
      out.write("                function renderFilterSummary() {\n");
      out.write("                    var params = new URLSearchParams(window.location.search);\n");
      out.write("                    var items = [];\n");
      out.write("                    var code = params.get('customerCode') || '';\n");
      out.write("                    var contact = params.get('contactPerson') || '';\n");
      out.write("                    var type = params.get('customerType') || '';\n");
      out.write("                    var status = params.get('status') || '';\n");
      out.write("                    var address = params.get('address') || '';\n");
      out.write("                    if (code) items.push('<span class=\"label label-primary\" style=\"margin-right:6px;\">Mã KH: ' + $('<div>').text(code).html() + '</span>');\n");
      out.write("                    if (contact) items.push('<span class=\"label label-info\" style=\"margin-right:6px;\">Người LH: ' + $('<div>').text(contact).html() + '</span>');\n");
      out.write("                    if (type) items.push('<span class=\"label label-success\" style=\"margin-right:6px;\">Loại: ' + $('<div>').text(type).html() + '</span>');\n");
      out.write("                    if (status) items.push('<span class=\"label label-warning\" style=\"margin-right:6px;\">Trạng thái: ' + $('<div>').text(status).html() + '</span>');\n");
      out.write("                    if (address) items.push('<span class=\"label label-default\" style=\"margin-right:6px;\">Địa chỉ: ' + $('<div>').text(address).html() + '</span>');\n");
      out.write("                    $('#filterSummary').html(items.join(''));\n");
      out.write("                }\n");
      out.write("                renderFilterSummary();\n");
      out.write("        });\n");
      out.write("\n");
      out.write("        function viewCustomer(id) {\n");
      out.write("            $.ajax({\n");
      out.write("                url: 'api/customers?action=get&id=' + id,\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                    success: function (response) {\n");
      out.write("                    if (response.success) {\n");
      out.write("                        var customer = response.data;\n");
      out.write("                        var content = '<div class=\"row\">' +\n");
      out.write("                            '<div class=\"col-md-6\"><h5>Thông tin cơ bản</h5>' +\n");
      out.write("                            '<p><strong>ID:</strong> ' + customer.id + '</p>' +\n");
      out.write("                            '<p><strong>Mã khách hàng:</strong> ' + customer.customerCode + '</p>' +\n");
      out.write("                            '<p><strong>Tên công ty:</strong> ' + customer.companyName + '</p>' +\n");
      out.write("                            '<p><strong>Người liên hệ:</strong> ' + customer.contactPerson + '</p>' +\n");
      out.write("                            '<p><strong>Email:</strong> ' + customer.email + '</p>' +\n");
      out.write("                            '<p><strong>Số điện thoại:</strong> ' + customer.phone + '</p>' +\n");
      out.write("                            '<p><strong>Địa chỉ:</strong> ' + customer.address + '</p>' +\n");
      out.write("                            '</div>' +\n");
      out.write("                            '<div class=\"col-md-6\"><h5>Thông tin bổ sung</h5>' +\n");
      out.write("                            '<p><strong>Mã số thuế:</strong> ' + (customer.taxCode || '-') + '</p>' +\n");
      out.write("                            '<p><strong>Loại khách hàng:</strong> ' + (customer.customerType === 'company' ? 'Doanh nghiệp' : 'Cá nhân') + '</p>' +\n");
      out.write("                            '<p><strong>Trạng thái:</strong> ' + (customer.status === 'active' ? 'Hoạt động' : 'Tạm khóa') + '</p>' +\n");
      out.write("                            '</div></div>';\n");
      out.write("                        $('#customerDetailContent').html(content);\n");
      out.write("                        $('#customerDetailModal').modal('show');\n");
      out.write("                    } else {\n");
      out.write("                        alert('Không thể tải thông tin khách hàng: ' + response.message);\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                    error: function () {\n");
      out.write("                    alert('Lỗi kết nối đến server');\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function editCustomer(id) {\n");
      out.write("            $.ajax({\n");
      out.write("                url: 'api/customers?action=get&id=' + id,\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                    success: function (response) {\n");
      out.write("                    if (response.success) {\n");
      out.write("                        var customer = response.data;\n");
      out.write("                        $('#customerCode').val(customer.customerCode);\n");
      out.write("                        $('#companyName').val(customer.companyName);\n");
      out.write("                        $('#userContract').val(customer.contactPerson);\n");
      out.write("                        $('#customerEmail').val(customer.email);\n");
      out.write("                        $('#customerPhone').val(customer.phone);\n");
      out.write("                        $('#customerAddress').val(customer.address);\n");
      out.write("                        $('#taxCode').val(customer.taxCode || '');\n");
      out.write("                        $('#customerType').val(customer.customerType);\n");
      out.write("                        currentEditingCustomer = customer;\n");
      out.write("                        $('#addCustomerModal .modal-title').text('Chỉnh sửa khách hàng');\n");
      out.write("                        $('#addCustomerModal').modal('show');\n");
      out.write("                    } else {\n");
      out.write("                        alert('Không thể tải thông tin khách hàng: ' + response.message);\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                    error: function () {\n");
      out.write("                    alert('Lỗi kết nối đến server');\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function deleteCustomer(id) {\n");
      out.write("            if (confirm('Bạn có chắc chắn muốn xóa khách hàng này?')) {\n");
      out.write("                $.ajax({\n");
      out.write("                    url: 'api/customers',\n");
      out.write("                    type: 'POST',\n");
      out.write("                    data: { action: 'delete', id: id },\n");
      out.write("                    dataType: 'json',\n");
      out.write("                        success: function (response) {\n");
      out.write("                        if (response.success) {\n");
      out.write("                            alert('Đã xóa khách hàng thành công');\n");
      out.write("                            location.reload();\n");
      out.write("                        } else {\n");
      out.write("                            alert('Lỗi khi xóa khách hàng: ' + response.message);\n");
      out.write("                        }\n");
      out.write("                    },\n");
      out.write("                        error: function () {\n");
      out.write("                        alert('Lỗi kết nối đến server');\n");
      out.write("                    }\n");
      out.write("                });\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function activateCustomer(id) {\n");
      out.write("            if (confirm('Bạn có chắc chắn muốn kích hoạt khách hàng này?')) {\n");
      out.write("                $.ajax({\n");
      out.write("                    url: 'api/customers',\n");
      out.write("                    type: 'POST',\n");
      out.write("                    data: { action: 'activate', id: id },\n");
      out.write("                    dataType: 'json',\n");
      out.write("                        success: function (response) {\n");
      out.write("                        if (response.success) {\n");
      out.write("                            alert('Đã kích hoạt khách hàng thành công');\n");
      out.write("                            location.reload();\n");
      out.write("                        } else {\n");
      out.write("                            alert('Lỗi khi kích hoạt khách hàng: ' + response.message);\n");
      out.write("                        }\n");
      out.write("                    },\n");
      out.write("                        error: function () {\n");
      out.write("                        alert('Lỗi kết nối đến server');\n");
      out.write("                    }\n");
      out.write("                });\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function saveCustomer() {\n");
      out.write("            var formData = {\n");
      out.write("                customerCode: $('#customerCode').val(),\n");
      out.write("                companyName: $('#companyName').val(),\n");
      out.write("                userContract: $('#userContract').val(),\n");
      out.write("                customerEmail: $('#customerEmail').val(),\n");
      out.write("                customerPhone: $('#customerPhone').val(),\n");
      out.write("                customerAddress: $('#customerAddress').val(),\n");
      out.write("                taxCode: $('#taxCode').val(),\n");
      out.write("                customerType: $('#customerType').val()\n");
      out.write("            };\n");
      out.write("\n");
      out.write("            // Kiểm tra các trường bắt buộc cơ bản\n");
      out.write("            if (!formData.customerCode || !formData.userContract || \n");
      out.write("                !formData.customerEmail || !formData.customerPhone || !formData.customerAddress || !formData.customerType) {\n");
      out.write("                alert('Vui lòng điền đầy đủ thông tin bắt buộc');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("\n");
      out.write("                // Ràng buộc: Mã khách hàng phải có cả chữ và số, chỉ cho phép ký tự chữ và số\n");
      out.write("                var code = (formData.customerCode || '').trim();\n");
      out.write("                var codeRegex = /^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z0-9]+$/;\n");
      out.write("                if (!codeRegex.test(code)) {\n");
      out.write("                    alert('Mã khách hàng phải bao gồm cả chữ và số, và chỉ chứa ký tự chữ/số.');\n");
      out.write("                    $('#customerCode').focus();\n");
      out.write("                    return;\n");
      out.write("                }\n");
      out.write("\n");
      out.write("                // Ràng buộc: Email phải là @gmail.com\n");
      out.write("                var email = (formData.customerEmail || '').trim();\n");
      out.write("                var emailRegex = /^[A-Za-z0-9._%+-]+@gmail\\.com$/i;\n");
      out.write("                if (!emailRegex.test(email)) {\n");
      out.write("                    alert('Email phải có định dạng @gmail.com');\n");
      out.write("                    $('#customerEmail').focus();\n");
      out.write("                    return;\n");
