      out.write("\r\n");
      out.write("                // Ràng buộc: Email phải là @gmail.com hoặc @fpt.edu.vn\r\n");
      out.write("                var email = (formData.customerEmail || '').trim();\r\n");
      out.write("                var emailRegex = /^[A-Za-z0-9._%+-]+@(gmail\\.com|fpt\\.edu\\.vn)$/i;\r\n");
