      out.write("        function sortTable(column, order) {\r\n");
      out.write("            var columnMap = { 'id': 0, 'customer_code': 1, 'company_name': 2, 'contact_person': 3, 'email': 4, 'phone': 5, 'address': 6, 'tax_code': 7, 'customer_type': 8, 'status': 9 };\r\n");
      out.write("            var colIdx = columnMap[column];\r\n");
