      out.write("                }\r\n");
      out.write("                var cmp = aVal.localeCompare(bVal, 'vi', { sensitivity: 'base' });\r\n");
      out.write("                return order === 'asc' ? cmp : -cmp;\r\n");
      out.write("            });\r\n");
      out.write("            $('#customersTable tbody').empty().append($rows);\r\n");
