      out.write("        // Helper functions\r\n");
      out.write("        function formatDateTime(dateString) {\r\n");
      out.write("            if (!dateString) return '-';\r\n");
      out.write("            try {\r\n");
      out.write("                var date = new Date(dateString);\r\n");
      out.write("                var day = String(date.getDate()).padStart(2, '0');\r\n");
      out.write("                var month = String(date.getMonth() + 1).padStart(2, '0');\r\n");
      out.write("                var year = date.getFullYear();\r\n");
      out.write("                var hours = String(date.getHours()).padStart(2, '0');\r\n");
      out.write("                var minutes = String(date.getMinutes()).padStart(2, '0');\r\n");
      out.write("                var seconds = String(date.getSeconds()).padStart(2, '0');\r\n");
      out.write("                return hours + ':' + minutes + ':' + seconds + ' ' + day + '/' + month + '/' + year;\r\n");
      out.write("            } catch (e) {\r\n");
      out.write("                return dateString;\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
