      out.write("\n");
      out.write("    <script>\n");
      out.write("        $('#noti-box').slimScroll({\n");
      out.write("            height: '400px',\n");
      out.write("            size: '5px',\n");
      out.write("            BorderRadius: '5px'\n");
      out.write("        });\n");
      out.write("        \n");
      out.write("        // Load support statistics\n");
      out.write("        $(document).ready(function() {\n");
      out.write("            loadSupportStats();\n");
      out.write("            loadRecentTickets();\n");
      out.write("        });\n");
      out.write("        \n");
      out.write("        function loadSupportStats() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: 'api/support-stats?action=getStats',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if (response.success) {\n");
      out.write("                        var stats = response.data;\n");
      out.write("                        \n");
      out.write("                        // Cập nhật thống kê chính\n");
      out.write("                        $('#urgentTickets').text(stats.urgentTickets || 0);\n");
      out.write("                        $('#totalOpenTickets').text(stats.totalOpenTickets || 0);\n");
      out.write("                        $('#resolvedToday').text(stats.resolvedToday || 0);\n");
      out.write("                        $('#satisfactionRate').text(stats.satisfactionRate || '0%');\n");
      out.write("                        \n");
      out.write("                        // Cập nhật thống kê theo danh mục\n");
      out.write("                        $('#technicalTickets').text(stats.technicalTickets || 0);\n");
      out.write("                        $('#billingTickets').text(stats.billingTickets || 0);\n");
      out.write("                        $('#generalTickets').text(stats.generalTickets || 0);\n");
      out.write("                        $('#complaintTickets').text(stats.complaintTickets || 0);\n");
      out.write("                        \n");
      out.write("                        // Cập nhật sidebar\n");
      out.write("                        $('#openTickets').text(stats.totalOpenTickets || 0);\n");
      out.write("                        $('#unreadMessages').text(stats.unreadMessages || 0);\n");
      out.write("                        $('#unreadNotifications').text(stats.unreadNotifications || 0);\n");
      out.write("                        \n");
      out.write("                        console.log('Đã tải thống kê từ database');\n");
      out.write("                    } else {\n");
      out.write("                        console.error('Lỗi tải thống kê:', response.message);\n");
      out.write("                        // Fallback về dữ liệu mặc định\n");
      out.write("                        loadDefaultStats();\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    console.error('Không thể tải thống kê hỗ trợ:', error);\n");
      out.write("                    // Fallback về dữ liệu mặc định\n");
      out.write("                    loadDefaultStats();\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadDefaultStats() {\n");
      out.write("            // Dữ liệu mặc định khi không kết nối được database\n");
      out.write("            $('#urgentTickets').text('0');\n");
      out.write("            $('#totalOpenTickets').text('0');\n");
      out.write("            $('#resolvedToday').text('0');\n");
      out.write("            $('#satisfactionRate').text('0%');\n");
      out.write("            \n");
      out.write("            $('#technicalTickets').text('0');\n");
      out.write("            $('#billingTickets').text('0');\n");
      out.write("            $('#generalTickets').text('0');\n");
      out.write("            $('#complaintTickets').text('0');\n");
      out.write("            \n");
      out.write("            $('#openTickets').text('0');\n");
      out.write("            $('#unreadMessages').text('0');\n");
      out.write("            $('#unreadNotifications').text('0');\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadRecentTickets() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: 'api/support-stats?action=getRecentTickets&limit=4',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if (response.success) {\n");
      out.write("                        var tickets = response.data;\n");
      out.write("                        var tbody = $('#recentTicketsTable');\n");
      out.write("                        tbody.empty();\n");
      out.write("                        \n");
      out.write("                        if (tickets.length === 0) {\n");
      out.write("                            tbody.append('<tr><td colspan=\"7\" class=\"text-center\">Chưa có yêu cầu hỗ trợ nào</td></tr>');\n");
      out.write("                        } else {\n");
      out.write("                            tickets.forEach(function(ticket) {\n");
      out.write("                                var row = createTicketRow(ticket);\n");
      out.write("                                tbody.append(row);\n");
      out.write("                            });\n");
      out.write("                        }\n");
      out.write("                        \n");
      out.write("                        console.log('Đã tải ' + tickets.length + ' ticket gần đây');\n");
      out.write("                    } else {\n");
      out.write("                        console.error('Lỗi tải danh sách ticket:', response.message);\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    console.error('Không thể tải danh sách ticket:', error);\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function createTicketRow(ticket) {\n");
      out.write("            var categoryLabel = getCategoryLabel(ticket.category);\n");
      out.write("            var priorityLabel = getPriorityLabel(ticket.priority);\n");
      out.write("            var statusLabel = getStatusLabel(ticket.status);\n");
      out.write("            var timeAgo = getTimeAgo(ticket.createdAt);\n");
      out.write("            \n");
      out.write("            return '<tr>' +\n");
      out.write("                '<td>#' + ticket.ticketNumber + '</td>' +\n");
      out.write("                '<td>' + (ticket.customerName || ticket.contactPerson || 'N/A') + '</td>' +\n");
      out.write("                '<td>' + ticket.subject + '</td>' +\n");
      out.write("                '<td>' + categoryLabel + '</td>' +\n");
      out.write("                '<td>' + priorityLabel + '</td>' +\n");
      out.write("                '<td>' + statusLabel + '</td>' +\n");
      out.write("                '<td>' + timeAgo + '</td>' +\n");
      out.write("            '</tr>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getCategoryLabel(category) {\n");
      out.write("            var labels = {\n");
      out.write("                'technical': '<span class=\"label label-info\">Kỹ thuật</span>',\n");
      out.write("                'billing': '<span class=\"label label-success\">Thanh toán</span>',\n");
      out.write("                'general': '<span class=\"label label-default\">Chung</span>',\n");
      out.write("                'complaint': '<span class=\"label label-danger\">Khiếu nại</span>'\n");
      out.write("            };\n");
      out.write("            return labels[category] || '<span class=\"label label-default\">' + category + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getPriorityLabel(priority) {\n");
      out.write("            var labels = {\n");
      out.write("                'low': '<span class=\"label label-info\">Thấp</span>',\n");
      out.write("                'medium': '<span class=\"label label-warning\">Trung bình</span>',\n");
      out.write("                'high': '<span class=\"label label-danger\">Cao</span>',\n");
      out.write("                'urgent': '<span class=\"label label-danger\">Khẩn cấp</span>'\n");
      out.write("            };\n");
      out.write("            return labels[priority] || '<span class=\"label label-default\">' + priority + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getStatusLabel(status) {\n");
      out.write("            var labels = {\n");
      out.write("                'open': '<span class=\"label label-primary\">Đang chờ</span>',\n");
      out.write("                'in_progress': '<span class=\"label label-warning\">Đang xử lý</span>',\n");
      out.write("                'resolved': '<span class=\"label label-success\">Hoàn thành</span>',\n");
      out.write("                'closed': '<span class=\"label label-default\">Đã đóng</span>'\n");
      out.write("            };\n");
      out.write("            return labels[status] || '<span class=\"label label-default\">' + status + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getTimeAgo(createdAt) {\n");
      out.write("            if (!createdAt) return 'N/A';\n");
      out.write("            \n");
      out.write("            var now = new Date();\n");
      out.write("            var created = new Date(createdAt);\n");
      out.write("            var diffMs = now - created;\n");
      out.write("            var diffMins = Math.floor(diffMs / 60000);\n");
      out.write("            var diffHours = Math.floor(diffMins / 60);\n");
      out.write("            var diffDays = Math.floor(diffHours / 24);\n");
      out.write("            \n");
      out.write("            if (diffMins < 1) return 'Vừa xong';\n");
      out.write("            if (diffMins < 60) return diffMins + ' phút trước';\n");
      out.write("            if (diffHours < 24) return diffHours + ' giờ trước';\n");
      out.write("            return diffDays + ' ngày trước';\n");
      out.write("        }\n");
      out.write("    </script>\n");
      out.write("    \n");
      out.write("    <script type=\"text/javascript\">\n");
      out.write("        $(function() {\n");
      out.write("            \"use strict\";\n");
