      out.write("                    },\n");
      out.write("                    {\n");
      out.write("                        label: \"Ticket khẩn cấp\",\n");
      out.write("                        fillColor: \"rgba(255,193,7,0.2)\",\n");
      out.write("                        strokeColor: \"rgba(255,193,7,1)\",\n");
      out.write("                        pointColor: \"rgba(255,193,7,1)\",\n");
      out.write("                        pointStrokeColor: \"#fff\",\n");
      out.write("                        pointHighlightFill: \"#fff\",\n");
      out.write("                        pointHighlightStroke: \"rgba(255,193,7,1)\",\n");
      out.write("                        data: [3, 5, 4, 6, 4, 2, 1]\n");
      out.write("                    }\n");
      out.write("                ]\n");
      out.write("            };\n");
      out.write("            new Chart(document.getElementById(\"linechart\").getContext(\"2d\")).Line(data,{\n");
      out.write("                responsive : true,\n");
      out.write("                maintainAspectRatio: false,\n");
      out.write("            });\n");
      out.write("        });\n");
      out.write("    </script>\n");
      out.write("</body>\n");
