      out.write("                    <button type=\"button\" class=\"btn btn-primary\" id=\"btnSaveWorkOrder\">\r\n");
      out.write("                        <i class=\"fa fa-save\"></i> Lưu thay đổi\r\n");
      out.write("                    </button>\r\n");
