      out.write("\r\n");
      out.write("    <!-- jQuery 2.0.2 -->\r\n");
      out.write("    <script src=\"http://ajax.googleapis.com/ajax/libs/jquery/2.0.2/jquery.min.js\"></script>\r\n");
      out.write("    <script src=\"js/jquery.min.js\" type=\"text/javascript\"></script>\r\n");
      out.write("    <!-- Bootstrap -->\r\n");
      out.write("    <script src=\"js/bootstrap.min.js\" type=\"text/javascript\"></script>\r\n");
      out.write("    <!-- DATA TABES SCRIPT -->\r\n");
      out.write("    <script src=\"js/plugins/datatables/jquery.dataTables.js\" type=\"text/javascript\"></script>\r\n");
      out.write("    <script src=\"js/plugins/datatables/dataTables.bootstrap.js\" type=\"text/javascript\"></script>\r\n");
      out.write("    <!-- Director App -->\r\n");
      out.write("    <script src=\"js/Director/app.js\" type=\"text/javascript\"></script>\r\n");
      out.write("\r\n");
      out.write("    <script>\r\n");
      out.write("        var ctx = '");
      out.print( request.getContextPath() );
      out.write("';\r\n");
      out.write("        var allWorkOrders = [];\r\n");
      out.write("        var filteredWorkOrders = [];\r\n");
      out.write("        var technicalStaff = [];\r\n");
      out.write("        \r\n");
      out.write("        $(document).ready(function() {\r\n");
      out.write("            loadTechnicalStaff();\r\n");
      out.write("            loadWorkOrders();\r\n");
      out.write("            \r\n");
      out.write("            // Filter button\r\n");
      out.write("            $('#btnFilter').click(function() {\r\n");
      out.write("                applyFilters();\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Reset button\r\n");
      out.write("            $('#btnReset').click(function() {\r\n");
      out.write("                $('#filterForm')[0].reset();\r\n");
      out.write("                filteredWorkOrders = allWorkOrders;\r\n");
      out.write("                renderTable();\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Save work order changes\r\n");
      out.write("            $('#btnSaveWorkOrder').click(function() {\r\n");
      out.write("                saveWorkOrderChanges();\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Enter to search\r\n");
      out.write("            $('#filterSearch').keypress(function(e) {\r\n");
      out.write("                if(e.which == 13) {\r\n");
      out.write("                    $('#btnFilter').click();\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        });\r\n");
      out.write("        \r\n");
      out.write("        function loadTechnicalStaff() {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/users?action=getTechnicalStaff',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        technicalStaff = response.data || [];\r\n");
      out.write("                        populateStaffDropdown();\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    console.log('Không thể tải danh sách nhân viên kỹ thuật');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function populateStaffDropdown() {\r\n");
      out.write("            var select = $('#detail_assigned_to');\r\n");
      out.write("            select.empty();\r\n");
      out.write("            select.append('<option value=\"\">Chưa phân công</option>');\r\n");
      out.write("            \r\n");
      out.write("            technicalStaff.forEach(function(staff) {\r\n");
      out.write("                select.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function loadWorkOrders() {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-orders?action=list',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        allWorkOrders = response.data || [];\r\n");
      out.write("                        filteredWorkOrders = allWorkOrders;\r\n");
      out.write("                        renderTable();\r\n");
      out.write("                        updateStatistics();\r\n");
      out.write("                    } else {\r\n");
      out.write("                        showError('Không thể tải dữ liệu');\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    showError('Lỗi kết nối máy chủ');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function updateStatistics() {\r\n");
      out.write("            var total = allWorkOrders.length;\r\n");
      out.write("            var pending = allWorkOrders.filter(w => w.status === 'pending').length;\r\n");
      out.write("            var inProgress = allWorkOrders.filter(w => w.status === 'in_progress').length;\r\n");
      out.write("            var completed = allWorkOrders.filter(w => w.status === 'completed').length;\r\n");
      out.write("            \r\n");
      out.write("            $('#totalWorkOrders').text(total);\r\n");
      out.write("            $('#pendingWorkOrders').text(pending);\r\n");
      out.write("            $('#inProgressWorkOrders').text(inProgress);\r\n");
      out.write("            $('#completedWorkOrders').text(completed);\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function applyFilters() {\r\n");
      out.write("            var status = $('#filterStatus').val();\r\n");
      out.write("            var priority = $('#filterPriority').val();\r\n");
      out.write("            var assignedTo = $('#filterAssignedTo').val();\r\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\r\n");
      out.write("            \r\n");
      out.write("            filteredWorkOrders = allWorkOrders.filter(function(workOrder) {\r\n");
      out.write("                var matchStatus = !status || workOrder.status === status;\r\n");
      out.write("                var matchPriority = !priority || workOrder.priority === priority;\r\n");
      out.write("                var matchAssigned = !assignedTo || \r\n");
      out.write("                    (assignedTo === 'unassigned' && !workOrder.assignedTo) ||\r\n");
      out.write("                    (assignedTo !== 'unassigned' && workOrder.assignedTo == assignedTo);\r\n");
      out.write("                var matchSearch = !search || \r\n");
      out.write("                    (workOrder.workOrderNumber && workOrder.workOrderNumber.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (workOrder.title && workOrder.title.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (workOrder.description && workOrder.description.toLowerCase().includes(search));\r\n");
      out.write("                \r\n");
      out.write("                return matchStatus && matchPriority && matchAssigned && matchSearch;\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            renderTable();\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function renderTable() {\r\n");
      out.write("            var tbody = $('#workOrdersTableBody');\r\n");
      out.write("            tbody.empty();\r\n");
      out.write("            \r\n");
      out.write("            if(!filteredWorkOrders || filteredWorkOrders.length === 0) {\r\n");
      out.write("                tbody.append('<tr><td colspan=\"8\" class=\"text-center\">Không có dữ liệu</td></tr>');\r\n");
      out.write("                return;\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("            filteredWorkOrders.forEach(function(workOrder) {\r\n");
      out.write("                var assignedName = workOrder.assignedToName || 'Chưa phân công';\r\n");
      out.write("                var row = '<tr>' +\r\n");
      out.write("                    '<td>' + (workOrder.workOrderNumber || '#' + workOrder.id) + '</td>' +\r\n");
      out.write("                    '<td>' + (workOrder.customerName || 'N/A') + '</td>' +\r\n");
      out.write("                    '<td>' + (workOrder.title || '') + '</td>' +\r\n");
      out.write("                    '<td>' + getPriorityBadge(workOrder.priority) + '</td>' +\r\n");
      out.write("                    '<td>' + getStatusBadge(workOrder.status) + '</td>' +\r\n");
      out.write("                    '<td>' + assignedName + '</td>' +\r\n");
      out.write("                    '<td>' + formatDate(workOrder.createdAt) + '</td>' +\r\n");
      out.write("                    '<td class=\"work-order-actions\">' +\r\n");
      out.write("                        '<button class=\"btn btn-info btn-view\" data-id=\"' + workOrder.id + '\"><i class=\"fa fa-eye\"></i> Xem</button>' +\r\n");
      out.write("                    '</td>' +\r\n");
      out.write("                '</tr>';\r\n");
      out.write("                tbody.append(row);\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Bind view button\r\n");
      out.write("            $('.btn-view').click(function() {\r\n");
      out.write("                var id = $(this).data('id');\r\n");
      out.write("                viewWorkOrderDetail(id);\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function getPriorityBadge(priority) {\r\n");
      out.write("            var labels = {\r\n");
      out.write("                'urgent': 'Khẩn cấp',\r\n");
      out.write("                'high': 'Cao',\r\n");
      out.write("                'medium': 'Trung bình',\r\n");
      out.write("                'low': 'Thấp'\r\n");
      out.write("            };\r\n");
      out.write("            var badge = priority ? 'badge-' + priority : '';\r\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[priority] || 'N/A') + '</span>';\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function getStatusBadge(status) {\r\n");
      out.write("            var labels = {\r\n");
      out.write("                'pending': 'Chờ xử lý',\r\n");
      out.write("                'in_progress': 'Đang thực hiện',\r\n");
      out.write("                'completed': 'Hoàn thành',\r\n");
      out.write("                'cancelled': 'Đã hủy'\r\n");
      out.write("            };\r\n");
      out.write("            var badge = status ? 'badge-' + status : '';\r\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[status] || 'N/A') + '</span>';\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function formatDate(dateStr) {\r\n");
      out.write("            if(!dateStr) return '';\r\n");
      out.write("            try {\r\n");
      out.write("                var date = new Date(dateStr);\r\n");
      out.write("                return date.toLocaleDateString('vi-VN') + ' ' + date.toLocaleTimeString('vi-VN');\r\n");
      out.write("            } catch(e) {\r\n");
      out.write("                return dateStr;\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function viewWorkOrderDetail(id) {\r\n");
      out.write("            var workOrder = allWorkOrders.find(function(w) { return w.id == id; });\r\n");
      out.write("            if(!workOrder) return;\r\n");
      out.write("            \r\n");
      out.write("            $('#detail_work_order_id').val(workOrder.id);\r\n");
      out.write("            $('#detail_work_order_number').text(workOrder.workOrderNumber || '#' + workOrder.id);\r\n");
      out.write("            $('#detail_customer').text(workOrder.customerName || 'N/A');\r\n");
      out.write("            $('#detail_title').text(workOrder.title || '');\r\n");
      out.write("            $('#detail_description').val(workOrder.description || '');\r\n");
      out.write("            $('#detail_priority').val(workOrder.priority || 'medium');\r\n");
      out.write("            $('#detail_status').val(workOrder.status || 'pending');\r\n");
      out.write("            $('#detail_assigned_to').val(workOrder.assignedTo || '');\r\n");
      out.write("            $('#detail_created').text(formatDate(workOrder.createdAt));\r\n");
      out.write("            $('#detail_estimated_hours').val(workOrder.estimatedHours || '');\r\n");
      out.write("            $('#detail_actual_hours').val(workOrder.actualHours || '');\r\n");
      out.write("            $('#detail_scheduled_date').val(workOrder.scheduledDate || '');\r\n");
      out.write("            $('#detail_completion_date').val(workOrder.completionDate || '');\r\n");
      out.write("            $('#detail_notes').val(workOrder.notes || '');\r\n");
      out.write("            \r\n");
      out.write("            $('#workOrderDetailModal').modal('show');\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function saveWorkOrderChanges() {\r\n");
      out.write("            var id = $('#detail_work_order_id').val();\r\n");
      out.write("            var data = {\r\n");
      out.write("                action: 'update',\r\n");
      out.write("                id: id,\r\n");
      out.write("                priority: $('#detail_priority').val(),\r\n");
      out.write("                status: $('#detail_status').val(),\r\n");
      out.write("                assignedTo: $('#detail_assigned_to').val(),\r\n");
      out.write("                estimatedHours: $('#detail_estimated_hours').val(),\r\n");
      out.write("                actualHours: $('#detail_actual_hours').val(),\r\n");
      out.write("                scheduledDate: $('#detail_scheduled_date').val(),\r\n");
      out.write("                completionDate: $('#detail_completion_date').val(),\r\n");
      out.write("                notes: $('#detail_notes').val()\r\n");
      out.write("            };\r\n");
      out.write("            \r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-orders',\r\n");
      out.write("                type: 'POST',\r\n");
      out.write("                data: data,\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        alert('Cập nhật thành công!');\r\n");
      out.write("                        $('#workOrderDetailModal').modal('hide');\r\n");
      out.write("                        loadWorkOrders();\r\n");
      out.write("                    } else {\r\n");
      out.write("                        alert('Lỗi: ' + (response.message || 'Không thể cập nhật'));\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    alert('Lỗi kết nối máy chủ');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
