      out.write("    <script>\r\n");
      out.write("        var ctx = '");
      out.print( request.getContextPath() );
      out.write("';\r\n");
      out.write("        var allWorkOrders = [];\r\n");
      out.write("        var technicalStaff = [];\r\n");
      out.write("        \r\n");
      out.write("        $(document).ready(function() {\r\n");
      out.write("            // Load all dashboard data\r\n");
      out.write("            loadWorkOrders();\r\n");
      out.write("            loadTechnicalStaff();\r\n");
      out.write("            loadTechTickets();\r\n");
      out.write("            \r\n");
      out.write("            // Initialize slimScroll for notifications\r\n");
      out.write("            $('#noti-box').slimScroll({\r\n");
      out.write("                height: '400px',\r\n");
      out.write("                size: '5px',\r\n");
      out.write("                BorderRadius: '5px'\r\n");
      out.write("            });\r\n");
      out.write("        });\r\n");
      out.write("        \r\n");
      out.write("        // Load Work Orders\r\n");
      out.write("        function loadWorkOrders() {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-orders?action=list',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        allWorkOrders = response.data || [];\r\n");
      out.write("                        updateWorkOrderStatistics();\r\n");
      out.write("                        renderRecentWorkOrders();\r\n");
      out.write("                        renderWorkOrdersChart();\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    console.log('Không thể tải dữ liệu work orders');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        // Update Work Order Statistics\r\n");
      out.write("        function updateWorkOrderStatistics() {\r\n");
      out.write("            var total = allWorkOrders.length;\r\n");
      out.write("            var pending = allWorkOrders.filter(w => w.status === 'pending').length;\r\n");
      out.write("            var inProgress = allWorkOrders.filter(w => w.status === 'in_progress').length;\r\n");
      out.write("            var completed = allWorkOrders.filter(w => w.status === 'completed').length;\r\n");
      out.write("            var urgent = allWorkOrders.filter(w => w.priority === 'urgent' || w.priority === 'high').length;\r\n");
      out.write("            \r\n");
      out.write("            // Calculate total estimated hours\r\n");
      out.write("            var totalHours = 0;\r\n");
