      out.write("                                    var norm = ts.indexOf('T') === -1 ? ts.replace(' ', 'T') : ts;\r\n");
      out.write("                                    d = new Date(norm);\r\n");
      out.write("                                    if(isNaN(d.getTime())) {\r\n");
      out.write("                                        var parts = ts.split(' ');\r\n");
      out.write("                                        var datePart = parts[0] || '';\r\n");
      out.write("                                        var timePart = parts[1] || '';\r\n");
      out.write("                                        var dp = datePart.split('-');\r\n");
      out.write("                                        if(dp.length === 3) {\r\n");
      out.write("                                            d = new Date(parseInt(dp[0],10), parseInt(dp[1],10)-1, parseInt(dp[2],10));\r\n");
      out.write("                                            if(timePart) {\r\n");
      out.write("                                                var tp = timePart.split(':');\r\n");
      out.write("                                                if(tp.length >= 2) {\r\n");
      out.write("                                                    d.setHours(parseInt(tp[0],10), parseInt(tp[1],10), tp[2] ? parseInt(tp[2],10) : 0);\r\n");
      out.write("                                                }\r\n");
      out.write("                                            }\r\n");
      out.write("                                        }\r\n");
      out.write("                                    }\r\n");
      out.write("                                } else {\r\n");
      out.write("                                    d = new Date(ts);\r\n");
      out.write("                                }\r\n");
      out.write("                                if(isNaN(d.getTime())) return '-';\r\n");
      out.write("                                var dd = String(d.getDate()).padStart(2,'0');\r\n");
      out.write("                                var mm = String(d.getMonth()+1).padStart(2,'0');\r\n");
      out.write("                                var yyyy = d.getFullYear();\r\n");
      out.write("                                var hh = String(d.getHours()).padStart(2,'0');\r\n");
      out.write("                                var min = String(d.getMinutes()).padStart(2,'0');\r\n");
