      out.write("\r\n");
      out.write("        function badgeForStatus(s){\r\n");
      out.write("            var map = {pending:'label-default', in_progress:'label-warning', completed:'label-success', cancelled:'label-default', rejected:'label-danger'};\r\n");
      out.write("            return '<span class=\"label ' + (map[s]||'label-default') + '\">' + (s||'') + '</span>';\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
      out.write("        var currentPage = 1;\r\n");
