      out.write("        function loadTasks(){\r\n");
      out.write("            var status = document.getElementById('statusFilter').value;\r\n");
      out.write("            var priority = document.getElementById('priorityFilter').value;\r\n");
      out.write("            var scheduledFrom = document.getElementById('scheduledFrom').value;\r\n");
      out.write("            var scheduledTo = document.getElementById('scheduledTo').value;\r\n");
      out.write("            var q = document.getElementById('q').value;\r\n");
      out.write("            var size = parseInt(document.getElementById('pageSize').value || '10', 10);\r\n");
      out.write("            $.getJSON('api/tasks', { action:'listAssigned', userId: currentUserId, status: status, priority: priority, scheduledFrom: scheduledFrom, scheduledTo: scheduledTo, q: q, page: currentPage, size: size }, function(res){\r\n");
      out.write("                var tbody = document.getElementById('taskBody');\r\n");
      out.write("                tbody.innerHTML = '';\r\n");
      out.write("                if(!res.success){ tbody.innerHTML = '<tr><td colspan=\"8\">' + (res.message||'Lỗi tải dữ liệu') + '</td></tr>'; return; }\r\n");
      out.write("                if(!res.data || res.data.length === 0){ tbody.innerHTML = '<tr><td colspan=\"8\" class=\"text-center text-muted\">Không có nhiệm vụ</td></tr>'; renderPagination(res.meta); return; }\r\n");
      out.write("                renderPagination(res.meta);\r\n");
      out.write("                function fmt(ts){\r\n");
      out.write("                    if(!ts) return '';\r\n");
      out.write("                    try{\r\n");
      out.write("                        var d;\r\n");
      out.write("                        if(typeof ts === 'number') {\r\n");
      out.write("                            d = new Date(ts);\r\n");
      out.write("                        } else if(typeof ts === 'string') {\r\n");
      out.write("                            // Normalize MySQL-like \"YYYY-MM-DD HH:mm:ss\" to ISO by replacing space with 'T'\r\n");
      out.write("                            var norm = ts.indexOf('T') === -1 ? ts.replace(' ', 'T') : ts;\r\n");
      out.write("                            d = new Date(norm);\r\n");
      out.write("                            if(isNaN(d.getTime())) {\r\n");
      out.write("                                // Fallback: parse parts manually (YYYY-MM-DD HH:mm:ss)\r\n");
      out.write("                                var parts = ts.split(' ');\r\n");
      out.write("                                var datePart = parts[0] || '';\r\n");
      out.write("                                var dp = datePart.split('-');\r\n");
      out.write("                                if(dp.length === 3) {\r\n");
      out.write("                                    d = new Date(parseInt(dp[0],10), parseInt(dp[1],10)-1, parseInt(dp[2],10));\r\n");
      out.write("                                }\r\n");
      out.write("                            }\r\n");
      out.write("                        } else {\r\n");
      out.write("                            d = new Date(ts);\r\n");
      out.write("                        }\r\n");
      out.write("                        if(isNaN(d.getTime())) return '';\r\n");
      out.write("                        var dd = String(d.getDate()).padStart(2,'0');\r\n");
      out.write("                        var mm = String(d.getMonth()+1).padStart(2,'0');\r\n");
      out.write("                        var yyyy = d.getFullYear();\r\n");
      out.write("                        return dd + '/' + mm + '/' + yyyy;\r\n");
      out.write("                    }catch(e){ return ''; }\r\n");
      out.write("                }\r\n");
      out.write("\r\n");
      out.write("                res.data.forEach(function(it){\r\n");
      out.write("                    var actions = [];\r\n");
      out.write("                    if(it.taskStatus === 'pending'){\r\n");
      out.write("                        actions.push('<button class=\"btn btn-xs btn-primary\" onclick=\"ack(' + it.taskId + ')\"><i class=\"fa fa-check\"></i> Nhận</button>');\r\n");
      out.write("                        actions.push('<button class=\"btn btn-xs btn-danger\" onclick=\"openRejectModal(' + it.taskId + ')\"><i class=\"fa fa-times\"></i> Từ chối</button>');\r\n");
      out.write("                    }\r\n");
      out.write("                    if(it.taskStatus === 'in_progress'){\r\n");
      out.write("                        actions.push('<button class=\"btn btn-xs btn-success\" onclick=\"openCompleteModal(' + it.taskId + ', \\'' + (it.taskNumber||'').replace(/'/g, \"\\\\'\") + '\\', \\'' + (it.taskDescription||'').replace(/'/g, \"\\\\'\") + '\\')\"><i class=\"fa fa-flag-checkered\"></i> Hoàn thành</button>');\r\n");
      out.write("                    }\r\n");
      out.write("                    if(it.taskStatus === 'completed'){\r\n");
      out.write("                        actions.push('<button class=\"btn btn-xs btn-info\" onclick=\"viewReport(' + it.taskId + ')\"><i class=\"fa fa-eye\"></i> Xem báo cáo</button>');\r\n");
      out.write("                    }\r\n");
      out.write("                    \r\n");
      out.write("                    var tr = document.createElement('tr');\r\n");
      out.write("                    tr.innerHTML = \r\n");
      out.write("                        '<td><div><strong>' + (it.workOrderNumber||'') + '</strong></div><div>' + (it.workOrderTitle||'') + '</div></td>' +\r\n");
      out.write("                        '<td><div><strong>' + (it.taskNumber||'') + '</strong></div><div>' + (it.taskDescription||'') + '</div></td>' +\r\n");
      out.write("                        '<td>' + badgeForStatus(it.taskStatus) + '</td>' +\r\n");
      out.write("                        '<td>' + (it.taskPriority||'') + '</td>' +\r\n");
      out.write("                        '<td>' + (fmt(it.startDate) || '<span class=\"text-muted\">-</span>') + '</td>' +\r\n");
      out.write("                        '<td>' + (fmt(it.completionDate) || '<span class=\"text-muted\">-</span>') + '</td>' +\r\n");
      out.write("                        '<td class=\"reason-cell\">' + (it.rejectionReason ? (function(txt){ var esc = String(txt).replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;'); return '<span class=\"text-danger reason-ellipsis\" title=\"' + esc + '\">' + esc + '</span>'; })(it.rejectionReason) : '<span class=\"text-muted\">-</span>') + '</td>' +\r\n");
      out.write("                        '<td>' + actions.join(' ') + '</td>';\r\n");
      out.write("                    tbody.appendChild(tr);\r\n");
      out.write("                });\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
      out.write("        function resetFilters(){\r\n");
      out.write("            document.getElementById('statusFilter').value = '';\r\n");
      out.write("            document.getElementById('priorityFilter').value = '';\r\n");
      out.write("            document.getElementById('scheduledFrom').value = '';\r\n");
      out.write("            document.getElementById('scheduledTo').value = '';\r\n");
