      out.write("\r\n");
      out.write("        function renderPagination(meta){\r\n");
      out.write("            var ul = document.getElementById('taskPagination');\r\n");
      out.write("            if(!meta){ ul.innerHTML = ''; return; }\r\n");
      out.write("            var totalPages = meta.totalPages || 1;\r\n");
      out.write("            var page = meta.page || 1;\r\n");
      out.write("            var html = '';\r\n");
      out.write("            function li(p, text, disabled, active){\r\n");
      out.write("                return '<li class=\"' + (disabled ? 'disabled' : '') + ' ' + (active ? 'active' : '') + '\"><a href=\"#\" onclick=\"return gotoPage(' + p + ');\">' + text + '</a></li>';\r\n");
      out.write("            }\r\n");
      out.write("            html += li(Math.max(1, page-1), '&laquo;', page===1, false);\r\n");
      out.write("            for(var i=1;i<=totalPages;i++){\r\n");
      out.write("                html += li(i, i, false, i===page);\r\n");
      out.write("            }\r\n");
      out.write("            html += li(Math.min(totalPages, page+1), '&raquo;', page===totalPages, false);\r\n");
      out.write("            ul.innerHTML = html;\r\n");
      out.write("        }\r\n");
