      out.write("        // Submit form báo cáo hoàn thành\r\n");
      out.write("        $('#completeTaskForm').on('submit', function(e){\r\n");
      out.write("            e.preventDefault();\r\n");
      out.write("            \r\n");
      out.write("            var taskId = $('#modalTaskId').val();\r\n");
      out.write("            var formData = new FormData();\r\n");
      out.write("            formData.append('action', 'complete');\r\n");
      out.write("            formData.append('id', taskId);\r\n");
      out.write("            formData.append('actualHours', $('#actualHours').val());\r\n");
      out.write("            formData.append('completionPercentage', $('#completionPercentage').val());\r\n");
      out.write("            formData.append('workDescription', $('#workDescription').val());\r\n");
      out.write("            formData.append('issuesFound', $('#issuesFound').val());\r\n");
      out.write("            formData.append('notes', $('#notes').val());\r\n");
      out.write("            \r\n");
      out.write("            // Thêm files\r\n");
      out.write("            var files = $('#attachments')[0].files;\r\n");
      out.write("            for(var i = 0; i < files.length; i++){\r\n");
      out.write("                formData.append('files', files[i]);\r\n");
