      out.write("\n");
      out.write("        function previewEditImage(url) {\n");
      out.write("            var wrap = document.getElementById('editImagePreview');\n");
      out.write("            var img = document.getElementById('editPreviewImg');\n");
      out.write("            \n");
      out.write("            if (!url || url.trim().length === 0) {\n");
      out.write("                wrap.style.display = 'none';\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Kiểm tra format URL cơ bản\n");
      out.write("            try {\n");
      out.write("                new URL(url);\n");
      out.write("            } catch (e) {\n");
      out.write("                wrap.style.display = 'none';\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Kiểm tra extension ảnh\n");
      out.write("            var lowerUrl = url.toLowerCase();\n");
      out.write("            var isImageUrl = lowerUrl.includes('.jpg') || lowerUrl.includes('.jpeg') || \n");
      out.write("                           lowerUrl.includes('.png') || lowerUrl.includes('.gif') || \n");
      out.write("                           lowerUrl.includes('.webp') || lowerUrl.includes('image') ||\n");
      out.write("                           lowerUrl.includes('photo') || lowerUrl.includes('picture');\n");
      out.write("            \n");
      out.write("            if (!isImageUrl) {\n");
      out.write("                wrap.style.display = 'none';\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Hiển thị preview\n");
      out.write("            img.src = url;\n");
      out.write("            img.onload = function() {\n");
      out.write("                wrap.style.display = 'block';\n");
      out.write("            };\n");
      out.write("            img.onerror = function() {\n");
      out.write("                wrap.style.display = 'none';\n");
      out.write("            };\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        \n");
