      out.write("        \n");
      out.write("        // Xử lý thông báo lỗi từ URL parameters\n");
      out.write("        $(document).ready(function() {\n");
      out.write("            var urlParams = new URLSearchParams(window.location.search);\n");
      out.write("            var errorDiv = $('#addProductError');\n");
      out.write("            \n");
      out.write("            if (urlParams.has('validation_error')) {\n");
