      out.write("                                '<p><strong>Đơn vị:</strong> ' + (product.unit || 'pcs') + '</p>' +\n");
      out.write("                                '<p><strong>Giá bán:</strong> ' + new Intl.NumberFormat('vi-VN').format(product.unitPrice || 0) + ' VNĐ</p>' +\n");
      out.write("                            '</div>' +\n");
