      out.write("                                '<p><strong>Thông số kỹ thuật:</strong> ' + (product.specifications || 'Không có') + '</p>' +\n");
      out.write("                                '<p><strong>Bảo hành:</strong> ' + (product.warrantyMonths || 0) + ' tháng</p>' +\n");
      out.write("                                '<p><strong>Trạng thái:</strong> <span class=\"label ' + (product.status === 'active' ? 'label-success' : 'label-warning') + '\">' + \n");
      out.write("                                    (product.status === 'active' ? 'Đang bán' : 'Ngừng bán') + '</span></p>' +\n");
      out.write("                                (product.imageUrl && product.imageUrl.trim() !== '' ? '<p><strong>Ảnh:</strong><br><img src=\"' + product.imageUrl + '\" style=\"max-width:200px;max-height:200px; border:1px solid #ddd; border-radius:4px;\"></p>' : '') +\n");
      out.write("                            '</div>' +\n");
