      out.write("            var priority = $('#filterPriority').val();\r\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\r\n");
      out.write("            \r\n");
      out.write("            filteredTickets = allTickets.filter(function(ticket) {\r\n");
      out.write("                var matchStatus = !status || ticket.status === status;\r\n");
      out.write("                var matchCategory = !category || ticket.category === category;\r\n");
      out.write("                var matchPriority = !priority || ticket.priority === priority;\r\n");
      out.write("                var matchSearch = !search || \r\n");
      out.write("                    (ticket.ticketNumber && ticket.ticketNumber.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (ticket.subject && ticket.subject.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (ticket.description && ticket.description.toLowerCase().includes(search));\r\n");
      out.write("                \r\n");
      out.write("                return matchStatus && matchCategory && matchPriority && matchSearch;\r\n");
      out.write("            });\r\n");
