      out.write("                var matchPriority = !priority || ticket.priority === priority;\n");
      out.write("                var matchSearch = !search || \n");
      out.write("                    (ticket.ticketNumber && ticket.ticketNumber.toLowerCase().includes(search)) ||\n");
      out.write("                    (ticket.subject && ticket.subject.toLowerCase().includes(search)) ||\n");
      out.write("                    (ticket.description && ticket.description.toLowerCase().includes(search));\n");
      out.write("                \n");
      out.write("                return matchStatus && matchCategory && matchPriority && matchSearch;\n");
