      out.write("                'complaint': 'danger'\n");
      out.write("            };\n");
      out.write("            return '<span class=\"label label-' + (colors[category] || 'default') + '\">' + \n");
      out.write("                   (labels[category] || category) + '</span>';\n");
      out.write("        }\n");
