      out.write("              <small class=\"text-muted\">Chỉ chấp nhận file ảnh: JPG, PNG, GIF, WEBP (tối đa 10MB)</small>\r\n");
      out.write("              <div id=\"imagePreviewModal\" style=\"margin-top: 10px; display: none;\">\r\n");
