      out.write("                <button type=\"button\" class=\"btn btn-sm btn-danger mt-2\" onclick=\"removeImageModal()\">Xóa ảnh</button>\r\n");
      out.write("              </div>\r\n");
      out.write("              <div id=\"imageSizeErrorModal\" class=\"text-danger\" style=\"display: none; margin-top: 5px;\"></div>\r\n");
