      out.write("            <div class=\"d-flex gap-2\">\r\n");
      out.write("              <button type=\"button\" class=\"btn btn-primary\" id=\"submitFeedbackBtnModal\" onclick=\"submitFeedbackFromModal()\">Gửi đánh giá</button>\r\n");
      out.write("              <button type=\"button\" class=\"btn btn-secondary\" id=\"cancelFeedbackBtnModal\" onclick=\"hideFeedbackFormInModal()\" style=\"display: none;\">Hủy</button>\r\n");
