      out.write("\r\n");
      out.write("<!-- Modal thông báo thành công - Simple alert style -->\r\n");
      out.write("<div class=\"modal fade success-modal\" id=\"successModal\" tabindex=\"-1\" aria-hidden=\"true\">\r\n");
      out.write("  <div class=\"modal-dialog\">\r\n");
      out.write("    <div class=\"modal-content\">\r\n");
      out.write("      <div class=\"modal-header\">\r\n");
      out.write("        <h5 class=\"modal-title\" id=\"successTitle\">Thông báo</h5>\r\n");
      out.write("      </div>\r\n");
      out.write("      <div class=\"modal-body\">\r\n");
      out.write("        <div id=\"successMessage\">Thao tác đã được thực hiện thành công.</div>\r\n");
      out.write("      </div>\r\n");
      out.write("      <div class=\"modal-footer\">\r\n");
      out.write("        <button type=\"button\" class=\"btn success-btn\" data-bs-dismiss=\"modal\">OK</button>\r\n");
      out.write("      </div>\r\n");
