      out.write("</div>\r\n");
      out.write("\r\n");
      out.write("<!-- Modal xác nhận hủy yêu cầu -->\r\n");
      out.write("<div class=\"modal fade success-modal\" id=\"confirmModal\" tabindex=\"-1\" aria-hidden=\"true\">\r\n");
      out.write("  <div class=\"modal-dialog\">\r\n");
      out.write("    <div class=\"modal-content\">\r\n");
      out.write("      <div class=\"modal-header\">\r\n");
      out.write("        <h5 class=\"modal-title\">Xác nhận</h5>\r\n");
      out.write("      </div>\r\n");
      out.write("      <div class=\"modal-body\">\r\n");
      out.write("        <div id=\"confirmMessage\">Bạn có chắc chắn muốn hủy yêu cầu này?</div>\r\n");
      out.write("      </div>\r\n");
      out.write("      <div class=\"modal-footer\">\r\n");
      out.write("        <button type=\"button\" class=\"btn cancel-btn\" data-bs-dismiss=\"modal\">Hủy</button>\r\n");
      out.write("        <button type=\"button\" class=\"btn success-btn\" id=\"confirmCancelBtn\">OK</button>\r\n");
      out.write("      </div>\r\n");
      out.write("    </div>\r\n");
      out.write("  </div>\r\n");
      out.write("</div>\r\n");
      out.write("\r\n");
      out.write("<script>\r\n");
      out.write("  // Context path - global scope để các hàm có thể truy cập\r\n");
      out.write("  var ctx = '");
      out.print(request.getContextPath());
      out.write("';\r\n");
      out.write("  \r\n");
      out.write("  // ========== Feedback Modal Functions (Global scope) ==========\r\n");
      out.write("  \r\n");
      out.write("  // Open feedback modal\r\n");
      out.write("  function openFeedbackModal(ticketId, ticketNumber) {\r\n");
      out.write("    document.getElementById('feedbackTicketNumber').textContent = ticketNumber || '#' + ticketId;\r\n");
      out.write("    document.getElementById('feedbackModal').setAttribute('data-current-ticket-id', ticketId);\r\n");
      out.write("    \r\n");
      out.write("    // Load existing feedback\r\n");
      out.write("    loadFeedbackForModal(ticketId);\r\n");
      out.write("    \r\n");
      out.write("    // Show modal\r\n");
      out.write("    const feedbackModal = bootstrap.Modal.getOrCreateInstance(document.getElementById('feedbackModal'));\r\n");
      out.write("    feedbackModal.show();\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Load feedback for modal\r\n");
      out.write("  function loadFeedbackForModal(ticketId) {\r\n");
      out.write("    fetch(ctx + '/api/feedback?action=getByTicketId&ticketId=' + encodeURIComponent(ticketId), {\r\n");
      out.write("      headers: { 'Accept': 'application/json' }\r\n");
      out.write("    })\r\n");
      out.write("    .then(r => r.json())\r\n");
      out.write("    .then(j => {\r\n");
      out.write("      if (j && j.success) {\r\n");
      out.write("        if (j.data) {\r\n");
      out.write("          // Có feedback - hiển thị\r\n");
      out.write("          displayFeedbackInModal(j.data);\r\n");
      out.write("        } else {\r\n");
      out.write("          // Chưa có feedback - hiển thị form\r\n");
      out.write("          showFeedbackFormInModal();\r\n");
      out.write("        }\r\n");
      out.write("      } else {\r\n");
      out.write("        // Lỗi hoặc chưa có feedback - hiển thị form\r\n");
      out.write("        showFeedbackFormInModal();\r\n");
      out.write("      }\r\n");
      out.write("    })\r\n");
      out.write("    .catch(error => {\r\n");
      out.write("      console.error('Error loading feedback:', error);\r\n");
      out.write("      showFeedbackFormInModal();\r\n");
      out.write("    });\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Display existing feedback in modal\r\n");
      out.write("  function displayFeedbackInModal(feedback) {\r\n");
      out.write("    document.getElementById('feedbackDisplayInModal').style.display = 'block';\r\n");
      out.write("    document.getElementById('feedbackFormInModal').style.display = 'none';\r\n");
      out.write("    \r\n");
      out.write("    // Hiển thị rating\r\n");
      out.write("    var ratingDisplay = document.getElementById('feedbackRatingDisplayModal');\r\n");
      out.write("    if (ratingDisplay) {\r\n");
      out.write("      ratingDisplay.textContent = feedback.ratingStars || '';\r\n");
      out.write("      ratingDisplay.title = feedback.ratingDisplay || '';\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Hiển thị comment\r\n");
      out.write("    var commentDisplay = document.getElementById('feedbackCommentDisplayModal');\r\n");
      out.write("    if (commentDisplay) {\r\n");
      out.write("      commentDisplay.textContent = feedback.comment || '(Không có nhận xét)';\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Hiển thị ảnh\r\n");
      out.write("    var imageDisplay = document.getElementById('feedbackImageDisplayModal');\r\n");
      out.write("    if (imageDisplay) {\r\n");
      out.write("      if (feedback.imagePath) {\r\n");
      out.write("        imageDisplay.innerHTML = '<img src=\"' + ctx + '/' + feedback.imagePath + '\" alt=\"Feedback image\" style=\"max-width: 300px; max-height: 300px; border-radius: 5px; border: 1px solid #ddd; margin-top: 10px;\">';\r\n");
      out.write("      } else {\r\n");
      out.write("        imageDisplay.innerHTML = '';\r\n");
      out.write("      }\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Hiển thị ngày\r\n");
      out.write("    var dateDisplay = document.getElementById('feedbackDateDisplayModal');\r\n");
      out.write("    if (dateDisplay && feedback.createdAt) {\r\n");
      out.write("      var date = new Date(feedback.createdAt);\r\n");
      out.write("      dateDisplay.textContent = date.toLocaleDateString('vi-VN') + ' ' + date.toLocaleTimeString('vi-VN', { hour: '2-digit', minute: '2-digit' });\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // KHÔNG lưu feedback ID - không cho phép chỉnh sửa\r\n");
      out.write("    // Xóa feedback ID nếu có để ngăn chặn việc cập nhật\r\n");
      out.write("    var formInModal = document.getElementById('feedbackFormInModal');\r\n");
      out.write("    if (formInModal) {\r\n");
      out.write("      formInModal.removeAttribute('data-feedback-id');\r\n");
      out.write("    }\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Show feedback form in modal\r\n");
      out.write("  function showFeedbackFormInModal() {\r\n");
      out.write("    document.getElementById('feedbackDisplayInModal').style.display = 'none';\r\n");
      out.write("    document.getElementById('feedbackFormInModal').style.display = 'block';\r\n");
      out.write("    document.getElementById('cancelFeedbackBtnModal').style.display = 'none';\r\n");
      out.write("    \r\n");
      out.write("    // Reset form\r\n");
      out.write("    document.getElementById('feedbackRatingModal').value = '0';\r\n");
      out.write("    document.getElementById('feedbackCommentModal').value = '';\r\n");
      out.write("    document.getElementById('feedbackImageModal').value = '';\r\n");
      out.write("    document.getElementById('imagePreviewModal').style.display = 'none';\r\n");
      out.write("    document.getElementById('imageSizeErrorModal').style.display = 'none';\r\n");
      out.write("    updateRatingDisplayModal(0);\r\n");
      out.write("    \r\n");
      out.write("    // Initialize rating stars for modal\r\n");
      out.write("    initRatingStarsModal();\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Preview image\r\n");
      out.write("  function previewImageModal(input) {\r\n");
      out.write("    var errorDiv = document.getElementById('imageSizeErrorModal');\r\n");
      out.write("    var previewDiv = document.getElementById('imagePreviewModal');\r\n");
      out.write("    var previewImg = document.getElementById('previewImgModal');\r\n");
      out.write("    \r\n");
      out.write("    if (input.files && input.files[0]) {\r\n");
      out.write("      var file = input.files[0];\r\n");
      out.write("      \r\n");
      out.write("      // Validate file size (10MB)\r\n");
      out.write("      if (file.size > 10 * 1024 * 1024) {\r\n");
      out.write("        errorDiv.textContent = 'Kích thước ảnh quá lớn. Tối đa 10MB.';\r\n");
      out.write("        errorDiv.style.display = 'block';\r\n");
      out.write("        input.value = '';\r\n");
      out.write("        previewDiv.style.display = 'none';\r\n");
      out.write("        return;\r\n");
      out.write("      }\r\n");
      out.write("      \r\n");
      out.write("      // Validate file type\r\n");
      out.write("      var validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'image/webp'];\r\n");
      out.write("      if (!validTypes.includes(file.type)) {\r\n");
      out.write("        errorDiv.textContent = 'Định dạng ảnh không hợp lệ. Chỉ chấp nhận JPG, PNG, GIF, WEBP.';\r\n");
      out.write("        errorDiv.style.display = 'block';\r\n");
      out.write("        input.value = '';\r\n");
      out.write("        previewDiv.style.display = 'none';\r\n");
      out.write("        return;\r\n");
      out.write("      }\r\n");
      out.write("      \r\n");
      out.write("      errorDiv.style.display = 'none';\r\n");
      out.write("      \r\n");
      out.write("      var reader = new FileReader();\r\n");
      out.write("      reader.onload = function(e) {\r\n");
      out.write("        previewImg.src = e.target.result;\r\n");
      out.write("        previewDiv.style.display = 'block';\r\n");
      out.write("      };\r\n");
      out.write("      reader.readAsDataURL(file);\r\n");
      out.write("    } else {\r\n");
      out.write("      previewDiv.style.display = 'none';\r\n");
      out.write("      errorDiv.style.display = 'none';\r\n");
      out.write("    }\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Remove image\r\n");
      out.write("  function removeImageModal() {\r\n");
      out.write("    document.getElementById('feedbackImageModal').value = '';\r\n");
      out.write("    document.getElementById('imagePreviewModal').style.display = 'none';\r\n");
      out.write("    document.getElementById('imageSizeErrorModal').style.display = 'none';\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Hide feedback form in modal\r\n");
      out.write("  function hideFeedbackFormInModal() {\r\n");
      out.write("    var ticketId = document.getElementById('feedbackModal').getAttribute('data-current-ticket-id');\r\n");
      out.write("    if (ticketId) {\r\n");
      out.write("      loadFeedbackForModal(ticketId);\r\n");
      out.write("    }\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Initialize rating stars for modal\r\n");
      out.write("  function initRatingStarsModal() {\r\n");
      out.write("    var stars = document.querySelectorAll('.rating-input-modal .star-modal');\r\n");
      out.write("    stars.forEach(function(star) {\r\n");
      out.write("      star.addEventListener('click', function() {\r\n");
      out.write("        var rating = parseInt(this.getAttribute('data-rating'));\r\n");
      out.write("        setRatingModal(rating);\r\n");
      out.write("      });\r\n");
      out.write("      star.addEventListener('mouseenter', function() {\r\n");
      out.write("        var rating = parseInt(this.getAttribute('data-rating'));\r\n");
      out.write("        highlightStarsModal(rating);\r\n");
      out.write("      });\r\n");
      out.write("    });\r\n");
      out.write("    \r\n");
      out.write("    var ratingInput = document.querySelector('.rating-input-modal');\r\n");
      out.write("    if (ratingInput) {\r\n");
      out.write("      ratingInput.addEventListener('mouseleave', function() {\r\n");
      out.write("        var currentRating = parseInt(document.getElementById('feedbackRatingModal').value) || 0;\r\n");
      out.write("        highlightStarsModal(currentRating);\r\n");
      out.write("      });\r\n");
      out.write("    }\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Set rating in modal\r\n");
      out.write("  function setRatingModal(rating) {\r\n");
      out.write("    document.getElementById('feedbackRatingModal').value = rating;\r\n");
      out.write("    updateRatingDisplayModal(rating);\r\n");
      out.write("  }\r\n");
      out.write("  \r\n");
      out.write("  // Highlight stars in modal\r\n");
      out.write("  function highlightStarsModal(rating) {\r\n");
      out.write("    var stars = document.querySelectorAll('.rating-input-modal .star-modal');\r\n");
      out.write("    stars.forEach(function(star) {\r\n");
      out.write("      var starRating = parseInt(star.getAttribute('data-rating'));\r\n");
      out.write("      if (starRating <= rating) {\r\n");
      out.write("        star.textContent = '★';\r\n");
      out.write("        star.style.color = '#ffc107';\r\n");
