      out.write("                            <div class=\"col-sm-9\">\n");
      out.write("                                <input type=\"text\" class=\"form-control\" id=\"work_order_priority_display\" readonly>\n");
      out.write("                                <input type=\"hidden\" id=\"work_order_priority\">\n");
      out.write("                            </div>\n");
      out.write("                        </div>\n");
      out.write("                        \n");
      out.write("                        <div class=\"form-group\">\n");
      out.write("                            <label class=\"col-sm-3 control-label\">Trạng thái:</label>\n");
      out.write("                            <div class=\"col-sm-9\">\n");
      out.write("                                <input type=\"text\" class=\"form-control\" id=\"work_order_status_display\" value=\"Đang xử lý\" readonly>\n");
      out.write("                                <input type=\"hidden\" id=\"work_order_status\" value=\"in_progress\">\n");
      out.write("                            </div>\n");
      out.write("                        </div>\n");
      out.write("                        \n");
      out.write("                        <div class=\"form-group\">\n");
