      out.write("                            <div class=\"col-sm-9\">\n");
      out.write("                                <input type=\"number\" class=\"form-control\" id=\"work_order_estimated_hours\" step=\"0.5\" min=\"0\" placeholder=\"Số giờ ước tính (VD: 2.5)\">\n");
      out.write("                                <small class=\"help-block\">Thời gian dự kiến hoàn thành (giờ)</small>\n");
      out.write("                            </div>\n");
      out.write("                        </div>\n");
      out.write("                        \n");
      out.write("                        <div class=\"form-group\">\n");
      out.write("                            <label class=\"col-sm-3 control-label\">Ngày hẹn thực hiện:</label>\n");
      out.write("                            <div class=\"col-sm-9\">\n");
      out.write("                                <input type=\"date\" class=\"form-control\" id=\"work_order_scheduled_date\">\n");
      out.write("                                <small class=\"help-block\">Ngày dự kiến bắt đầu thực hiện công việc</small>\n");
      out.write("                            </div>\n");
      out.write("                        </div>\n");
      out.write("                        \n");
      out.write("                    </form>\n");
