      out.write("                <div class=\"modal-footer\">\n");
      out.write("                    <button type=\"button\" class=\"btn btn-success\" id=\"btnConfirmCreateWorkOrder\">\n");
      out.write("                        <i class=\"fa fa-plus\"></i> Tạo đơn hàng\n");
      out.write("                    </button>\n");
      out.write("                    <button type=\"button\" class=\"btn btn-default\" data-dismiss=\"modal\">Hủy</button>\n");
      out.write("                </div>\n");
      out.write("            </div>\n");
      out.write("        </div>\n");
      out.write("    </div>\n");
      out.write("\n");
      out.write("    <!-- jQuery 2.0.2 -->\n");
      out.write("    <script src=\"http://ajax.googleapis.com/ajax/libs/jquery/2.0.2/jquery.min.js\"></script>\n");
      out.write("    <script src=\"js/jquery.min.js\" type=\"text/javascript\"></script>\n");
      out.write("    <!-- Bootstrap -->\n");
      out.write("    <script src=\"js/bootstrap.min.js\" type=\"text/javascript\"></script>\n");
      out.write("    <!-- DATA TABES SCRIPT -->\n");
      out.write("    <script src=\"js/plugins/datatables/jquery.dataTables.js\" type=\"text/javascript\"></script>\n");
      out.write("    <script src=\"js/plugins/datatables/dataTables.bootstrap.js\" type=\"text/javascript\"></script>\n");
      out.write("    <!-- Director App -->\n");
      out.write("    <script src=\"js/Director/app.js\" type=\"text/javascript\"></script>\n");
      out.write("\n");
      out.write("    <script>\n");
      out.write("        var ctx = '");
      out.print( request.getContextPath() );
      out.write("';\n");
      out.write("        var allTickets = [];\n");
      out.write("        var filteredTickets = [];\n");
      out.write("        \n");
      out.write("        $(document).ready(function() {\n");
      out.write("            loadTickets();\n");
      out.write("            \n");
      out.write("            // Check if there's a forwarded ticket in localStorage\n");
      out.write("            var forwardedTicket = localStorage.getItem('forwardedTicket');\n");
      out.write("            if(forwardedTicket) {\n");
      out.write("                try {\n");
      out.write("                    var ticket = JSON.parse(forwardedTicket);\n");
      out.write("                    // Thêm ticket được chuyển tiếp vào danh sách\n");
      out.write("                    allTickets.push(ticket);\n");
      out.write("                    filteredTickets = allTickets;\n");
      out.write("                    renderTable();\n");
      out.write("                    updateStatistics();\n");
      out.write("                    // Xóa thông tin đã xử lý\n");
      out.write("                    localStorage.removeItem('forwardedTicket');\n");
      out.write("                    // Auto-open the ticket detail modal after data is loaded\n");
      out.write("                    setTimeout(function() {\n");
      out.write("                        viewTicketDetail(ticket.id);\n");
      out.write("                    }, 1000);\n");
      out.write("                } catch(e) {\n");
      out.write("                    console.log('Error parsing forwarded ticket:', e);\n");
      out.write("                }\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Filter button\n");
      out.write("            $('#btnFilter').click(function() {\n");
      out.write("                applyFilters();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Reset button\n");
      out.write("            $('#btnReset').click(function() {\n");
      out.write("                $('#filterForm')[0].reset();\n");
      out.write("                filteredTickets = allTickets;\n");
      out.write("                renderTable();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Confirm create work order\n");
      out.write("            $('#btnConfirmCreateWorkOrder').click(function() {\n");
      out.write("                confirmCreateWorkOrder();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Enter to search\n");
      out.write("            $('#filterSearch').keypress(function(e) {\n");
      out.write("                if(e.which == 13) {\n");
      out.write("                    $('#btnFilter').click();\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        });\n");
      out.write("        \n");
      out.write("        function loadTickets() {\n");
      out.write("            // Gọi API mới để lấy danh sách ticket technical\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/tech-support?action=list',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if (response.success) {\n");
      out.write("                        // Tất cả ticket đã được lọc ở backend (chỉ technical)\n");
      out.write("                        allTickets = response.data || [];\n");
      out.write("                        filteredTickets = allTickets;\n");
      out.write("                        renderTable();\n");
      out.write("                        updateStatistics();\n");
      out.write("                        console.log('Đã tải ' + allTickets.length + ' ticket kỹ thuật');\n");
      out.write("                        // Debug: Kiểm tra customerId trong tickets\n");
      out.write("                        if (allTickets.length > 0) {\n");
      out.write("                            console.log('Sample ticket có customerId:', allTickets[0].customerId);\n");
      out.write("                            console.log('Sample ticket object:', allTickets[0]);\n");
      out.write("                        }\n");
      out.write("                        \n");
      out.write("                        // Hiển thị thông báo nếu có ticket mới\n");
      out.write("                        var newTickets = allTickets.filter(function(t) {\n");
      out.write("                            return t.status === 'open' || t.status === 'in_progress';\n");
      out.write("                        });\n");
      out.write("                        \n");
      out.write("                        if (newTickets.length > 0) {\n");
      out.write("                            $('#forwardedAlert').show();\n");
      out.write("                            $('#forwardedAlert').html('<i class=\"fa fa-info-circle\"></i> <strong>Thông báo:</strong> Có ' + newTickets.length + ' yêu cầu hỗ trợ kỹ thuật cần xử lý.');\n");
      out.write("                            setTimeout(function() {\n");
      out.write("                                $('#forwardedAlert').fadeOut();\n");
      out.write("                            }, 8000);\n");
      out.write("                        }\n");
      out.write("                    } else {\n");
      out.write("                        console.error('Lỗi tải danh sách ticket:', response.message);\n");
      out.write("                        showError('Không thể tải danh sách yêu cầu hỗ trợ: ' + response.message);\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    console.error('Không thể tải danh sách ticket:', error);\n");
      out.write("                    showError('Lỗi kết nối server');\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Load statistics\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/tech-support?action=stats',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if (response.success && response.data) {\n");
      out.write("                        $('#totalTickets').text(response.data.total || 0);\n");
      out.write("                        $('#openTickets').text(response.data.open || 0);\n");
      out.write("                        $('#inProgressTickets').text(response.data.inProgress || 0);\n");
      out.write("                        $('#resolvedTickets').text(response.data.resolved || 0);\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    console.error('Không thể tải thống kê:', error);\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function updateStatistics() {\n");
      out.write("            var total = allTickets.length;\n");
      out.write("            var open = allTickets.filter(t => t.status === 'open').length;\n");
      out.write("            var inProgress = allTickets.filter(t => t.status === 'in_progress').length;\n");
      out.write("            var resolved = allTickets.filter(t => t.status === 'resolved').length;\n");
      out.write("            \n");
      out.write("            $('#totalTickets').text(total);\n");
      out.write("            $('#openTickets').text(open);\n");
      out.write("            $('#inProgressTickets').text(inProgress);\n");
      out.write("            $('#resolvedTickets').text(resolved);\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function applyFilters() {\n");
      out.write("            var status = $('#filterStatus').val();\n");
      out.write("            var category = $('#filterCategory').val();\n");
      out.write("            var priority = $('#filterPriority').val();\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\n");
      out.write("            \n");
      out.write("            filteredTickets = allTickets.filter(function(ticket) {\n");
      out.write("                var matchStatus = !status || ticket.status === status;\n");
      out.write("                var matchCategory = !category || ticket.category === category;\n");
      out.write("                var matchPriority = !priority || ticket.priority === priority;\n");
      out.write("                var matchSearch = !search || \n");
      out.write("                    (ticket.ticketNumber && ticket.ticketNumber.toLowerCase().includes(search)) ||\n");
      out.write("                    (ticket.subject && ticket.subject.toLowerCase().includes(search)) ||\n");
      out.write("                    (ticket.description && ticket.description.toLowerCase().includes(search));\n");
      out.write("                \n");
      out.write("                return matchStatus && matchCategory && matchPriority && matchSearch;\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            renderTable();\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function renderTable() {\n");
      out.write("            var tbody = $('#ticketsTableBody');\n");
      out.write("            tbody.empty();\n");
      out.write("            \n");
      out.write("            if(!filteredTickets || filteredTickets.length === 0) {\n");
      out.write("                tbody.append('<tr><td colspan=\"8\" class=\"text-center\">Không có dữ liệu</td></tr>');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("             filteredTickets.forEach(function(ticket) {\n");
      out.write("                 var userRole = '");
      out.print( userRole );
      out.write("';\n");
      out.write("                 var actionButtons = '<button class=\"btn btn-info btn-view\" data-id=\"' + ticket.id + '\"><i class=\"fa fa-eye\"></i> Xem</button>';\n");
      out.write("                 \n");
      out.write("                 // Chỉ hiển thị nút \"Tạo WO\" cho head_technician và admin\n");
      out.write("                 if (userRole === 'head_technician' || userRole === 'admin') {\n");
      out.write("                     actionButtons += '<button class=\"btn btn-success btn-create-work-order\" data-id=\"' + ticket.id + '\"><i class=\"fa fa-plus\"></i> Tạo WO</button>';\n");
      out.write("                 }\n");
      out.write("                 \n");
      out.write("                 var row = '<tr>' +\n");
      out.write("                     '<td><strong>' + (ticket.ticketNumber || '#' + ticket.id) + '</strong></td>' +\n");
      out.write("                     '<td>' + (ticket.customerName || 'N/A') + '</td>' +\n");
      out.write("                     '<td>' + (ticket.subject || '') + '</td>' +\n");
      out.write("                     '<td>' + getCategoryBadge(ticket.category) + '</td>' +\n");
      out.write("                     '<td>' + getPriorityBadge(ticket.priority) + '</td>' +\n");
      out.write("                     '<td>' + getStatusBadge(ticket.status) + '</td>' +\n");
      out.write("                     '<td>' + formatDate(ticket.createdAt) + '</td>' +\n");
      out.write("                     '<td class=\"ticket-actions\">' + actionButtons + '</td>' +\n");
      out.write("                 '</tr>';\n");
      out.write("                 tbody.append(row);\n");
      out.write("             });\n");
      out.write("            \n");
      out.write("            // Bind view button\n");
      out.write("            $('.btn-view').click(function() {\n");
      out.write("                var id = $(this).data('id');\n");
      out.write("                viewTicketDetail(id);\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Bind create work order button\n");
      out.write("            $('.btn-create-work-order').click(function() {\n");
      out.write("                var id = $(this).data('id');\n");
      out.write("                showCreateWorkOrderModal(id);\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getCategoryBadge(category) {\n");
      out.write("            var labels = {\n");
      out.write("                'technical': 'Kỹ thuật',\n");
      out.write("                'billing': 'Thanh toán',\n");
      out.write("                'general': 'Chung',\n");
      out.write("                'complaint': 'Khiếu nại'\n");
      out.write("            };\n");
      out.write("            var colors = {\n");
      out.write("                'technical': 'info',\n");
      out.write("                'billing': 'success',\n");
      out.write("                'general': 'default',\n");
      out.write("                'complaint': 'danger'\n");
      out.write("            };\n");
      out.write("            return '<span class=\"label label-' + (colors[category] || 'default') + '\">' + \n");
      out.write("                   (labels[category] || category) + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getPriorityBadge(priority) {\n");
      out.write("            var labels = {\n");
      out.write("                'urgent': 'Khẩn cấp',\n");
      out.write("                'high': 'Cao',\n");
      out.write("                'medium': 'Trung bình',\n");
      out.write("                'low': 'Thấp'\n");
      out.write("            };\n");
      out.write("            var badge = priority ? 'badge-' + priority : '';\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[priority] || 'N/A') + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function getStatusBadge(status) {\n");
      out.write("            var labels = {\n");
      out.write("                'open': 'Chờ xử lý',\n");
      out.write("                'in_progress': 'Đang xử lý',\n");
      out.write("                'resolved': 'Đã giải quyết',\n");
      out.write("                'closed': 'Đã đóng'\n");
      out.write("            };\n");
      out.write("            var badge = status ? 'badge-' + status : '';\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[status] || 'N/A') + '</span>';\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function formatDate(dateStr) {\n");
      out.write("            if(!dateStr) return '';\n");
      out.write("            try {\n");
      out.write("                var date = new Date(dateStr);\n");
      out.write("                return date.toLocaleDateString('vi-VN') + ' ' + date.toLocaleTimeString('vi-VN');\n");
      out.write("            } catch(e) {\n");
      out.write("                return dateStr;\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function viewTicketDetail(id) {\n");
      out.write("            var ticket = allTickets.find(function(t) { return t.id == id; });\n");
      out.write("            if(!ticket) return;\n");
      out.write("            \n");
      out.write("            $('#detail_ticket_id').val(ticket.id);\n");
