      out.write("                $('#filterForm')[0].reset();\r\n");
      out.write("                filteredTickets = allTickets;\r\n");
      out.write("                renderTable();\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Confirm create work order\r\n");
      out.write("            $('#btnConfirmCreateWorkOrder').click(function() {\r\n");
      out.write("                confirmCreateWorkOrder();\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Enter to search\r\n");
      out.write("            $('#filterSearch').keypress(function(e) {\r\n");
      out.write("                if(e.which == 13) {\r\n");
      out.write("                    $('#btnFilter').click();\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        });\r\n");
      out.write("        \r\n");
      out.write("        function loadTickets() {\r\n");
      out.write("            // Gọi API mới để lấy danh sách ticket technical\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/tech-support?action=list',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if (response.success) {\r\n");
      out.write("                        // Tất cả ticket đã được lọc ở backend (chỉ technical)\r\n");
      out.write("                        allTickets = response.data || [];\r\n");
      out.write("                        filteredTickets = allTickets;\r\n");
      out.write("                        renderTable();\r\n");
      out.write("                        updateStatistics();\r\n");
      out.write("                        console.log('Đã tải ' + allTickets.length + ' ticket kỹ thuật');\r\n");
      out.write("                        // Debug: Kiểm tra customerId trong tickets\r\n");
      out.write("                        if (allTickets.length > 0) {\r\n");
      out.write("                            console.log('Sample ticket có customerId:', allTickets[0].customerId);\r\n");
      out.write("                            console.log('Sample ticket object:', allTickets[0]);\r\n");
      out.write("                        }\r\n");
      out.write("                        \r\n");
      out.write("                        // Hiển thị thông báo nếu có ticket mới\r\n");
      out.write("                        var newTickets = allTickets.filter(function(t) {\r\n");
      out.write("                            return t.status === 'open' || t.status === 'in_progress';\r\n");
      out.write("                        });\r\n");
      out.write("                        \r\n");
      out.write("                        if (newTickets.length > 0) {\r\n");
      out.write("                            $('#forwardedAlert').show();\r\n");
      out.write("                            $('#forwardedAlert').html('<i class=\"fa fa-info-circle\"></i> <strong>Thông báo:</strong> Có ' + newTickets.length + ' yêu cầu hỗ trợ kỹ thuật cần xử lý.');\r\n");
      out.write("                            setTimeout(function() {\r\n");
      out.write("                                $('#forwardedAlert').fadeOut();\r\n");
      out.write("                            }, 8000);\r\n");
      out.write("                        }\r\n");
      out.write("                    } else {\r\n");
      out.write("                        console.error('Lỗi tải danh sách ticket:', response.message);\r\n");
      out.write("                        showError('Không thể tải danh sách yêu cầu hỗ trợ: ' + response.message);\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function(xhr, status, error) {\r\n");
      out.write("                    console.error('Không thể tải danh sách ticket:', error);\r\n");
      out.write("                    showError('Lỗi kết nối server');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Load statistics\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/tech-support?action=stats',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if (response.success && response.data) {\r\n");
      out.write("                        $('#totalTickets').text(response.data.total || 0);\r\n");
      out.write("                        $('#openTickets').text(response.data.open || 0);\r\n");
      out.write("                        $('#inProgressTickets').text(response.data.inProgress || 0);\r\n");
      out.write("                        $('#resolvedTickets').text(response.data.resolved || 0);\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function(xhr, status, error) {\r\n");
      out.write("                    console.error('Không thể tải thống kê:', error);\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function updateStatistics() {\r\n");
      out.write("            var total = allTickets.length;\r\n");
      out.write("            var open = allTickets.filter(t => t.status === 'open').length;\r\n");
      out.write("            var inProgress = allTickets.filter(t => t.status === 'in_progress').length;\r\n");
      out.write("            var resolved = allTickets.filter(t => t.status === 'resolved').length;\r\n");
      out.write("            \r\n");
      out.write("            $('#totalTickets').text(total);\r\n");
      out.write("            $('#openTickets').text(open);\r\n");
      out.write("            $('#inProgressTickets').text(inProgress);\r\n");
      out.write("            $('#resolvedTickets').text(resolved);\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function applyFilters() {\r\n");
      out.write("            var status = $('#filterStatus').val();\r\n");
      out.write("            var category = $('#filterCategory').val();\r\n");
      out.write("            var priority = $('#filterPriority').val();\r\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\r\n");
      out.write("            \r\n");
      out.write("            filteredTickets = allTickets.filter(function(ticket) {\r\n");
      out.write("                var matchStatus = !status || ticket.status === status;\r\n");
      out.write("                var matchCategory = !category || ticket.category === category;\r\n");
      out.write("                var matchPriority = !priority || ticket.priority === priority;\r\n");
      out.write("                var matchSearch = !search || \r\n");
      out.write("                    (ticket.ticketNumber && ticket.ticketNumber.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (ticket.subject && ticket.subject.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (ticket.description && ticket.description.toLowerCase().includes(search));\r\n");
      out.write("                \r\n");
      out.write("                return matchStatus && matchCategory && matchPriority && matchSearch;\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            renderTable();\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function renderTable() {\r\n");
      out.write("            var tbody = $('#ticketsTableBody');\r\n");
      out.write("            tbody.empty();\r\n");
      out.write("            \r\n");
      out.write("            if(!filteredTickets || filteredTickets.length === 0) {\r\n");
      out.write("                tbody.append('<tr><td colspan=\"8\" class=\"text-center\">Không có dữ liệu</td></tr>');\r\n");
      out.write("                return;\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("             filteredTickets.forEach(function(ticket) {\r\n");
      out.write("                 var userRole = '");
      out.print( userRole );
      out.write("';\r\n");
      out.write("                 var actionButtons = '<button class=\"btn btn-info btn-view\" data-id=\"' + ticket.id + '\"><i class=\"fa fa-eye\"></i> Xem</button>';\r\n");
      out.write("                 \r\n");
      out.write("                 // Chỉ hiển thị nút \"Tạo WO\" cho head_technician và admin\r\n");
      out.write("                 if (userRole === 'head_technician' || userRole === 'admin') {\r\n");
      out.write("                     actionButtons += '<button class=\"btn btn-success btn-create-work-order\" data-id=\"' + ticket.id + '\"><i class=\"fa fa-plus\"></i> Tạo WO</button>';\r\n");
      out.write("                 }\r\n");
      out.write("                 \r\n");
      out.write("                 var row = '<tr>' +\r\n");
      out.write("                     '<td><strong>' + (ticket.ticketNumber || '#' + ticket.id) + '</strong></td>' +\r\n");
      out.write("                     '<td>' + (ticket.customerName || 'N/A') + '</td>' +\r\n");
      out.write("                     '<td>' + (ticket.subject || '') + '</td>' +\r\n");
      out.write("                     '<td>' + getCategoryBadge(ticket.category) + '</td>' +\r\n");
