      out.write("            \r\n");
      out.write("            // Build URL with parameters\r\n");
      out.write("            var url = ctx + '/api/technical-staff-management?action=list';\r\n");
      out.write("            if (status) {\r\n");
      out.write("                url += '&status=' + encodeURIComponent(status);\r\n");
      out.write("            }\r\n");
      out.write("            if (priority) {\r\n");
