      out.write("            var completedCount = staff.tasks.filter(t => t.status === 'completed').length;\r\n");
      out.write("            var totalCount = staff.tasks.length;\r\n");
      out.write("            \r\n");
      out.write("            var card = $('<div class=\"staff-card\"></div>');\r\n");
