      out.write("            // Header\r\n");
      out.write("            var header = $('<div class=\"staff-header\"></div>');\r\n");
      out.write("            header.append('<h4><i class=\"fa fa-user\"></i> ' + (staff.fullName || 'N/A') + '</h4>');\r\n");
      out.write("            header.append('<small><i class=\"fa fa-envelope\"></i> ' + (staff.email || 'N/A') + '</small>');\r\n");
      out.write("            card.append(header);\r\n");
      out.write("            \r\n");
      out.write("            // Info\r\n");
      out.write("            var info = $('<div class=\"staff-info\"></div>');\r\n");
      out.write("            info.append('<p><strong>ID:</strong> ' + staff.id + ' | <strong>Vai trò:</strong> ' + (staff.role || 'technical_staff') + '</p>');\r\n");
      out.write("            card.append(info);\r\n");
      out.write("            \r\n");
      out.write("            // Stats\r\n");
