      out.write("    <!-- Modal Báo cáo -->\n");
      out.write("    <div class=\"modal fade\" id=\"reportModal\" tabindex=\"-1\" role=\"dialog\">\n");
      out.write("        <div class=\"modal-dialog modal-lg\">\n");
      out.write("            <div class=\"modal-content\">\n");
