      out.write("                                                $('#taskEstimatedHours').after('<small class=\"help-block\">' + warningMsg + '</small>');\r\n");
      out.write("                                            }\r\n");
      out.write("                                        }\r\n");
      out.write("                                    } else {\r\n");
      out.write("                                        // Update help text with remaining hours\r\n");
      out.write("                                        var helpText = 'Tối thiểu: 0.1h, Tối đa: ' + workOrderHours.toFixed(1) + 'h (giờ ước tính của đơn hàng). ';\r\n");
      out.write("                                        helpText += 'Đã sử dụng: ' + totalEstimatedHours.toFixed(1) + 'h, Còn lại: ' + (workOrderHours - totalEstimatedHours).toFixed(1) + 'h';\r\n");
