      out.write("    <!-- DATA TABES SCRIPT -->\n");
      out.write("    <script src=\"js/plugins/datatables/jquery.dataTables.js\" type=\"text/javascript\"></script>\n");
      out.write("    <script src=\"js/plugins/datatables/dataTables.bootstrap.js\" type=\"text/javascript\"></script>\n");
      out.write("    <!-- Director App -->\n");
      out.write("    <script src=\"js/Director/app.js\" type=\"text/javascript\"></script>\n");
      out.write("\n");
      out.write("    <script>\n");
      out.write("        var ctx = '");
      out.print( request.getContextPath() );
      out.write("';\n");
      out.write("        var allWorkOrders = [];\n");
      out.write("        var filteredWorkOrders = [];\n");
      out.write("        var technicalStaff = [];\n");
      out.write("        \n");
      out.write("        $(document).ready(function() {\n");
      out.write("            loadTechnicalStaff();\n");
      out.write("            loadWorkOrders();\n");
      out.write("            \n");
      out.write("            // Filter button\n");
      out.write("            $('#btnFilter').click(function() {\n");
      out.write("                applyFilters();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Reset button\n");
      out.write("            $('#btnReset').click(function() {\n");
      out.write("                $('#filterForm')[0].reset();\n");
      out.write("                filteredWorkOrders = allWorkOrders;\n");
      out.write("                renderTable();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Save work order changes\n");
      out.write("            $('#btnSaveWorkOrder').click(function() {\n");
      out.write("                saveWorkOrderChanges();\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Enter to search\n");
      out.write("            $('#filterSearch').keypress(function(e) {\n");
      out.write("                if(e.which == 13) {\n");
      out.write("                    $('#btnFilter').click();\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        });\n");
      out.write("        \n");
      out.write("        function loadTechnicalStaff() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/users?role=technical_staff',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if(response && response.success) {\n");
      out.write("                        technicalStaff = response.data || [];\n");
      out.write("                        populateStaffDropdown();\n");
      out.write("                        // Also populate filter dropdown\n");
      out.write("                        var filterSelect = $('#filterAssignedTo');\n");
      out.write("                        technicalStaff.forEach(function(staff) {\n");
      out.write("                            filterSelect.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\n");
      out.write("                        });\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function() {\n");
      out.write("                    console.log('Không thể tải danh sách nhân viên kỹ thuật');\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function populateStaffDropdown() {\n");
      out.write("            var select = $('#detail_assigned_to');\n");
      out.write("            select.empty();\n");
      out.write("            select.append('<option value=\"\">Chưa phân công</option>');\n");
      out.write("            \n");
      out.write("            technicalStaff.forEach(function(staff) {\n");
      out.write("                select.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadWorkOrders() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/work-orders?action=list',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if(response && response.success) {\n");
      out.write("                        allWorkOrders = response.data || [];\n");
      out.write("                        filteredWorkOrders = allWorkOrders;\n");
      out.write("                        renderTable();\n");
      out.write("                        updateStatistics();\n");
      out.write("                    } else {\n");
      out.write("                        showError('Không thể tải dữ liệu: ' + (response.message || 'Unknown error'));\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    showError('Lỗi kết nối máy chủ: ' + error);\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function updateStatistics() {\n");
      out.write("            var total = allWorkOrders.length;\n");
      out.write("            var pending = allWorkOrders.filter(w => w.status === 'pending').length;\n");
      out.write("            var inProgress = allWorkOrders.filter(w => w.status === 'in_progress').length;\n");
      out.write("            var completed = allWorkOrders.filter(w => w.status === 'completed').length;\n");
      out.write("            \n");
      out.write("            $('#totalWorkOrders').text(total);\n");
      out.write("            $('#pendingWorkOrders').text(pending);\n");
      out.write("            $('#inProgressWorkOrders').text(inProgress);\n");
      out.write("            $('#completedWorkOrders').text(completed);\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function applyFilters() {\n");
      out.write("            var status = $('#filterStatus').val();\n");
      out.write("            var priority = $('#filterPriority').val();\n");
      out.write("            var assignedTo = $('#filterAssignedTo').val();\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\n");
      out.write("            \n");
      out.write("            filteredWorkOrders = allWorkOrders.filter(function(workOrder) {\n");
      out.write("                var matchStatus = !status || workOrder.status === status;\n");
      out.write("                var matchPriority = !priority || workOrder.priority === priority;\n");
      out.write("                var matchAssigned = !assignedTo || \n");
      out.write("                    (assignedTo === 'unassigned' && !workOrder.assignedTo) ||\n");
      out.write("                    (assignedTo !== 'unassigned' && workOrder.assignedTo == assignedTo);\n");
      out.write("                var matchSearch = !search || \n");
      out.write("                    (workOrder.workOrderNumber && workOrder.workOrderNumber.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.title && workOrder.title.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.description && workOrder.description.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.customerName && workOrder.customerName.toLowerCase().includes(search));\n");
      out.write("                \n");
      out.write("                return matchStatus && matchPriority && matchAssigned && matchSearch;\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            renderTable();\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function renderTable() {\n");
      out.write("            var tbody = $('#workOrdersTableBody');\n");
      out.write("            tbody.empty();\n");
      out.write("            \n");
      out.write("            if(!filteredWorkOrders || filteredWorkOrders.length === 0) {\n");
      out.write("                tbody.append('<tr><td colspan=\"9\" class=\"text-center\">Không có dữ liệu</td></tr>');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            filteredWorkOrders.forEach(function(workOrder) {\n");
      out.write("                var assignedName = workOrder.assignedToName || 'Chưa phân công';\n");
