      out.write("            $('#assignTaskModal').on('hidden.bs.modal', function() {\r\n");
      out.write("                var workOrderId = $('#assign_work_order_id').val();\r\n");
      out.write("                if(workOrderId) {\r\n");
      out.write("                    // Reload assigned users to update the display\r\n");
      out.write("                    reloadAssignedUsersForWorkOrder(workOrderId);\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        });\r\n");
      out.write("        \r\n");
      out.write("        function loadTechnicalStaff() {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/users?role=technical_staff',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        technicalStaff = response.data || [];\r\n");
      out.write("                        populateStaffDropdown();\r\n");
      out.write("                        // Also populate filter dropdown\r\n");
      out.write("                        var filterSelect = $('#filterAssignedTo');\r\n");
      out.write("                        technicalStaff.forEach(function(staff) {\r\n");
      out.write("                            filterSelect.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\r\n");
      out.write("                        });\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    console.log('Không thể tải danh sách nhân viên kỹ thuật');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function populateStaffDropdown() {\r\n");
      out.write("            var select = $('#detail_assigned_to');\r\n");
      out.write("            select.empty();\r\n");
      out.write("            select.append('<option value=\"\">Chưa phân công</option>');\r\n");
      out.write("            \r\n");
      out.write("            technicalStaff.forEach(function(staff) {\r\n");
      out.write("                select.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function loadWorkOrders() {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-orders?action=list',\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                timeout: 10000,\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    console.log('Work orders response:', response);\r\n");
      out.write("                    if(response && response.success) {\r\n");
      out.write("                        allWorkOrders = response.data || [];\r\n");
      out.write("                        filteredWorkOrders = allWorkOrders;\r\n");
      out.write("                        console.log('Loaded ' + allWorkOrders.length + ' work orders');\r\n");
      out.write("                        updateStatistics();\r\n");
      out.write("                        // Load assigned users for all work orders (will call renderTable when done)\r\n");
      out.write("                        loadAllAssignedUsers();\r\n");
      out.write("                    } else {\r\n");
      out.write("                        console.error('Failed to load work orders:', response);\r\n");
      out.write("                        showError('Không thể tải dữ liệu: ' + (response ? response.message : 'Unknown error'));\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function(xhr, status, error) {\r\n");
      out.write("                    console.error('Error loading work orders:', status, error, xhr);\r\n");
      out.write("                    showError('Lỗi kết nối máy chủ: ' + error);\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function loadAllAssignedUsers() {\r\n");
      out.write("            // Reset map\r\n");
      out.write("            workOrderAssignedUsers = {};\r\n");
      out.write("            \r\n");
      out.write("            if(allWorkOrders.length === 0) {\r\n");
      out.write("                renderTable();\r\n");
      out.write("                return;\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("            // Initialize all work orders with empty array first\r\n");
      out.write("            allWorkOrders.forEach(function(workOrder) {\r\n");
      out.write("                workOrderAssignedUsers[workOrder.id] = [];\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Render table immediately with empty assigned users\r\n");
      out.write("            renderTable();\r\n");
      out.write("            \r\n");
      out.write("            // Load tasks for all work orders\r\n");
      out.write("            var promises = allWorkOrders.map(function(workOrder) {\r\n");
      out.write("                return $.ajax({\r\n");
      out.write("                    url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrder.id,\r\n");
      out.write("                    type: 'GET',\r\n");
      out.write("                    dataType: 'json',\r\n");
      out.write("                    timeout: 5000\r\n");
      out.write("                }).then(function(response) {\r\n");
      out.write("                    if(response && response.success && response.data) {\r\n");
      out.write("                        var assignedUsers = new Set();\r\n");
      out.write("                        response.data.forEach(function(task) {\r\n");
      out.write("                            if(task.assignedToName && task.assignedToName.trim() !== '') {\r\n");
      out.write("                                assignedUsers.add(task.assignedToName);\r\n");
      out.write("                            }\r\n");
      out.write("                        });\r\n");
      out.write("                        workOrderAssignedUsers[workOrder.id] = Array.from(assignedUsers).sort();\r\n");
      out.write("                    } else {\r\n");
      out.write("                        workOrderAssignedUsers[workOrder.id] = [];\r\n");
      out.write("                    }\r\n");
      out.write("                }).catch(function(error) {\r\n");
      out.write("                    console.log('Error loading tasks for work order ' + workOrder.id + ':', error);\r\n");
      out.write("                    workOrderAssignedUsers[workOrder.id] = [];\r\n");
      out.write("                });\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Wait for all requests to complete, then render table again with updated data\r\n");
      out.write("            $.when.apply($, promises).always(function() {\r\n");
      out.write("                renderTable();\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function reloadAssignedUsersForWorkOrder(workOrderId) {\r\n");
      out.write("            // Reload assigned users for a specific work order\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrderId,\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success && response.data) {\r\n");
      out.write("                        var assignedUsers = new Set();\r\n");
      out.write("                        response.data.forEach(function(task) {\r\n");
      out.write("                            if(task.assignedToName && task.assignedToName.trim() !== '') {\r\n");
      out.write("                                assignedUsers.add(task.assignedToName);\r\n");
      out.write("                            }\r\n");
      out.write("                        });\r\n");
      out.write("                        workOrderAssignedUsers[workOrderId] = Array.from(assignedUsers).sort();\r\n");
      out.write("                    } else {\r\n");
      out.write("                        workOrderAssignedUsers[workOrderId] = [];\r\n");
      out.write("                    }\r\n");
      out.write("                    // Re-render table to update the display\r\n");
      out.write("                    renderTable();\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    workOrderAssignedUsers[workOrderId] = [];\r\n");
      out.write("                    renderTable();\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function updateStatistics() {\r\n");
      out.write("            var total = allWorkOrders.length;\r\n");
      out.write("            var pending = allWorkOrders.filter(w => w.status === 'pending').length;\r\n");
      out.write("            var inProgress = allWorkOrders.filter(w => w.status === 'in_progress').length;\r\n");
      out.write("            var completed = allWorkOrders.filter(w => w.status === 'completed').length;\r\n");
      out.write("            \r\n");
      out.write("            $('#totalWorkOrders').text(total);\r\n");
      out.write("            $('#pendingWorkOrders').text(pending);\r\n");
      out.write("            $('#inProgressWorkOrders').text(inProgress);\r\n");
      out.write("            $('#completedWorkOrders').text(completed);\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function applyFilters() {\r\n");
      out.write("            var status = $('#filterStatus').val();\r\n");
      out.write("            var priority = $('#filterPriority').val();\r\n");
      out.write("            var assignedTo = $('#filterAssignedTo').val();\r\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\r\n");
      out.write("            \r\n");
      out.write("            filteredWorkOrders = allWorkOrders.filter(function(workOrder) {\r\n");
      out.write("                var matchStatus = !status || workOrder.status === status;\r\n");
      out.write("                var matchPriority = !priority || workOrder.priority === priority;\r\n");
      out.write("                \r\n");
      out.write("                // Filter theo assigned users từ tasks\r\n");
      out.write("                var assignedUsersList = workOrderAssignedUsers[workOrder.id] || [];\r\n");
      out.write("                var matchAssigned = true;\r\n");
      out.write("                if(assignedTo) {\r\n");
      out.write("                    if(assignedTo === 'unassigned') {\r\n");
      out.write("                        // Chưa phân công - không có ai trong danh sách assigned users\r\n");
      out.write("                        matchAssigned = assignedUsersList.length === 0;\r\n");
      out.write("                    } else {\r\n");
      out.write("                        // Kiểm tra xem có nhân viên nào trong danh sách assigned users không\r\n");
      out.write("                        // assignedTo là user ID, cần kiểm tra trong danh sách assigned users\r\n");
      out.write("                        matchAssigned = false;\r\n");
      out.write("                        // Tìm trong danh sách technical staff để so sánh\r\n");
      out.write("                        var staff = technicalStaff.find(function(s) { return s.id == assignedTo; });\r\n");
      out.write("                        if(staff && assignedUsersList.length > 0) {\r\n");
      out.write("                            matchAssigned = assignedUsersList.some(function(userName) {\r\n");
      out.write("                                return userName === staff.fullName;\r\n");
      out.write("                            });\r\n");
      out.write("                        }\r\n");
      out.write("                    }\r\n");
      out.write("                }\r\n");
      out.write("                \r\n");
      out.write("                var matchSearch = !search || \r\n");
      out.write("                    (workOrder.workOrderNumber && workOrder.workOrderNumber.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (workOrder.title && workOrder.title.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (workOrder.description && workOrder.description.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (workOrder.customerName && workOrder.customerName.toLowerCase().includes(search)) ||\r\n");
      out.write("                    (assignedUsersList.length > 0 && assignedUsersList.some(function(userName) {\r\n");
      out.write("                        return userName.toLowerCase().includes(search);\r\n");
      out.write("                    }));\r\n");
      out.write("                \r\n");
      out.write("                return matchStatus && matchPriority && matchAssigned && matchSearch;\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            renderTable();\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function renderTable() {\r\n");
      out.write("            var tbody = $('#workOrdersTableBody');\r\n");
      out.write("            tbody.empty();\r\n");
      out.write("            \r\n");
      out.write("            if(!filteredWorkOrders || filteredWorkOrders.length === 0) {\r\n");
      out.write("                tbody.append('<tr><td colspan=\"9\" class=\"text-center\">Không có dữ liệu</td></tr>');\r\n");
      out.write("                return;\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("            filteredWorkOrders.forEach(function(workOrder) {\r\n");
      out.write("                // Get assigned users from tasks - tổng hợp tất cả nhân viên đã được phân công từ các task\r\n");
      out.write("                var assignedUsersList = workOrderAssignedUsers[workOrder.id] || [];\r\n");
      out.write("                var assignedDisplay = '';\r\n");
      out.write("                if(assignedUsersList.length > 0) {\r\n");
      out.write("                    // Hiển thị danh sách nhân viên với badge\r\n");
      out.write("                    assignedDisplay = assignedUsersList.map(function(userName) {\r\n");
      out.write("                        return '<span class=\"label label-success\" style=\"margin-right: 3px; display: inline-block;\">' + userName + '</span>';\r\n");
      out.write("                    }).join('');\r\n");
      out.write("                } else {\r\n");
      out.write("                    assignedDisplay = '<span class=\"text-muted\">Chưa phân công</span>';\r\n");
      out.write("                }\r\n");
      out.write("                \r\n");
      out.write("                var customerName = workOrder.customerName || 'N/A';\r\n");
      out.write("                var estimatedHours = workOrder.estimatedHours ? workOrder.estimatedHours + 'h' : '-';\r\n");
      out.write("                var row = '<tr>' +\r\n");
      out.write("                    '<td><strong>' + (workOrder.workOrderNumber || '#' + workOrder.id) + '</strong></td>' +\r\n");
      out.write("                    '<td>' + customerName + '</td>' +\r\n");
      out.write("                    '<td>' + (workOrder.title || '') + '</td>' +\r\n");
      out.write("                    '<td>' + getPriorityBadge(workOrder.priority) + '</td>' +\r\n");
      out.write("                    '<td>' + getStatusBadge(workOrder.status) + '</td>' +\r\n");
      out.write("                    '<td>' + assignedDisplay + '</td>' +\r\n");
      out.write("                    '<td class=\"text-center\">' + estimatedHours + '</td>' +\r\n");
      out.write("                    '<td>' + formatDate(workOrder.createdAt) + '</td>' +\r\n");
      out.write("                    '<td class=\"work-order-actions\">' +\r\n");
      out.write("                        '<button class=\"btn btn-info btn-view\" data-id=\"' + workOrder.id + '\" title=\"Xem chi tiết\">' +\r\n");
      out.write("                            '<i class=\"fa fa-eye\"></i>' +\r\n");
      out.write("                        '</button>' +\r\n");
      out.write("                        '<button class=\"btn btn-success btn-assign-task\" data-id=\"' + workOrder.id + '\" title=\"Chia việc\">' +\r\n");
      out.write("                            '<i class=\"fa fa-tasks\"></i>' +\r\n");
      out.write("                        '</button>' +\r\n");
      out.write("                        '<button class=\"btn btn-warning btn-report\" data-id=\"' + workOrder.id + '\" title=\"Báo cáo\">' +\r\n");
      out.write("                            '<i class=\"fa fa-file-text-o\"></i>' +\r\n");
      out.write("                        '</button>' +\r\n");
      out.write("                    '</td>' +\r\n");
      out.write("                '</tr>';\r\n");
      out.write("                tbody.append(row);\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Bind view button\r\n");
      out.write("            $('.btn-view').click(function() {\r\n");
      out.write("                var id = $(this).data('id');\r\n");
      out.write("                viewWorkOrderDetail(id);\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Bind assign task button\r\n");
      out.write("            $('.btn-assign-task').click(function() {\r\n");
      out.write("                var id = $(this).data('id');\r\n");
      out.write("                openAssignTaskModal(id);\r\n");
      out.write("            });\r\n");
      out.write("            \r\n");
      out.write("            // Bind report button\r\n");
      out.write("            $('.btn-report').click(function() {\r\n");
      out.write("                var id = $(this).data('id');\r\n");
      out.write("                openReportModal(id);\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function getPriorityBadge(priority) {\r\n");
      out.write("            var labels = {\r\n");
      out.write("                'urgent': 'Khẩn cấp',\r\n");
      out.write("                'high': 'Cao',\r\n");
      out.write("                'medium': 'Trung bình',\r\n");
      out.write("                'low': 'Thấp'\r\n");
      out.write("            };\r\n");
      out.write("            var badge = priority ? 'badge-' + priority : '';\r\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[priority] || 'N/A') + '</span>';\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function getStatusBadge(status) {\r\n");
      out.write("            var labels = {\r\n");
      out.write("                'pending': 'Chờ xử lý',\r\n");
      out.write("                'in_progress': 'Đang thực hiện',\r\n");
      out.write("                'completed': 'Hoàn thành',\r\n");
      out.write("                'cancelled': 'Đã hủy',\r\n");
      out.write("                'rejected': 'Đã từ chối'\r\n");
      out.write("            };\r\n");
      out.write("            var badge = status ? 'badge-' + status : '';\r\n");
      out.write("            // Nếu là rejected, dùng màu đỏ (có thể dùng badge-danger)\r\n");
      out.write("            if(status === 'rejected') {\r\n");
      out.write("                badge = 'badge-danger';\r\n");
      out.write("            }\r\n");
      out.write("            return '<span class=\"badge ' + badge + '\">' + (labels[status] || 'N/A') + '</span>';\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function formatDate(dateStr) {\r\n");
      out.write("            if(!dateStr) return '';\r\n");
      out.write("            try {\r\n");
      out.write("                var date = new Date(dateStr);\r\n");
      out.write("                var day = date.getDate().toString().padStart(2, '0');\r\n");
      out.write("                var month = (date.getMonth() + 1).toString().padStart(2, '0');\r\n");
      out.write("                var year = date.getFullYear();\r\n");
      out.write("                return day + '/' + month + '/' + year;\r\n");
      out.write("            } catch(e) {\r\n");
      out.write("                return dateStr;\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function viewWorkOrderDetail(id) {\r\n");
      out.write("            var workOrder = allWorkOrders.find(function(w) { return w.id == id; });\r\n");
      out.write("            if(!workOrder) return;\r\n");
      out.write("            \r\n");
      out.write("            $('#detail_work_order_id').val(workOrder.id);\r\n");
      out.write("            $('#detail_work_order_number').text(workOrder.workOrderNumber || '#' + workOrder.id);\r\n");
      out.write("            $('#detail_customer').text(workOrder.customerName || 'N/A');\r\n");
      out.write("            $('#detail_title').val(workOrder.title || '');\r\n");
      out.write("            $('#detail_description').val(workOrder.description || '');\r\n");
      out.write("            $('#detail_priority').val(workOrder.priority || 'medium');\r\n");
      out.write("            $('#detail_status').val(workOrder.status || 'pending');\r\n");
      out.write("            $('#detail_created').text(formatDate(workOrder.createdAt));\r\n");
      out.write("            $('#detail_estimated_hours').val(workOrder.estimatedHours || '');\r\n");
      out.write("            $('#detail_actual_hours').val(workOrder.actualHours || '');\r\n");
      out.write("            $('#detail_scheduled_date').val(workOrder.scheduledDate || '');\r\n");
      out.write("            $('#detail_completion_date').val(workOrder.completionDate || '');\r\n");
      out.write("            \r\n");
      out.write("            // Load danh sách người đã được phân công từ các task\r\n");
      out.write("            loadAssignedUsers(id);\r\n");
      out.write("            \r\n");
      out.write("            $('#workOrderDetailModal').modal('show');\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function loadAssignedUsers(workOrderId) {\r\n");
      out.write("            $.ajax({\r\n");
      out.write("                url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrderId,\r\n");
      out.write("                type: 'GET',\r\n");
      out.write("                dataType: 'json',\r\n");
      out.write("                success: function(response) {\r\n");
      out.write("                    if(response && response.success && response.data) {\r\n");
      out.write("                        var assignedUsers = new Set();\r\n");
      out.write("                        response.data.forEach(function(task) {\r\n");
      out.write("                            if(task.assignedToName && task.assignedToName.trim() !== '') {\r\n");
      out.write("                                assignedUsers.add(task.assignedToName);\r\n");
      out.write("                            }\r\n");
      out.write("                        });\r\n");
      out.write("                        \r\n");
      out.write("                        var assignedList = Array.from(assignedUsers).sort();\r\n");
      out.write("                        if(assignedList.length > 0) {\r\n");
      out.write("                            $('#detail_assigned_to').val(assignedList.join('\\n'));\r\n");
      out.write("                        } else {\r\n");
      out.write("                            $('#detail_assigned_to').val('Chưa có nhân viên nào được phân công');\r\n");
      out.write("                        }\r\n");
      out.write("                    } else {\r\n");
      out.write("                        $('#detail_assigned_to').val('Chưa có nhân viên nào được phân công');\r\n");
      out.write("                    }\r\n");
      out.write("                },\r\n");
      out.write("                error: function() {\r\n");
      out.write("                    $('#detail_assigned_to').val('Không thể tải danh sách phân công');\r\n");
      out.write("                }\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function saveWorkOrderChanges() {\r\n");
      out.write("            var id = $('#detail_work_order_id').val();\r\n");
      out.write("            var data = {\r\n");
      out.write("                action: 'update',\r\n");
      out.write("                id: id,\r\n");
      out.write("                estimatedHours: $('#detail_estimated_hours').val(),\r\n");
