      out.write("                            $(self).after('<div id=\"technical_solution_warning\" class=\"alert alert-warning\" style=\"margin-top: 5px; padding: 5px 10px; font-size: 12px;\"><i class=\"fa fa-exclamation-triangle\"></i> Nội dung đã được tự động cắt xuống 1000 ký tự.</div>');\n");
      out.write("                            setTimeout(function() {\n");
      out.write("                                $('#technical_solution_warning').fadeOut(function() {\n");
      out.write("                                    $(this).remove();\n");
      out.write("                                });\n");
