      out.write("            \n");
      out.write("            // Reset task filter\n");
      out.write("            $(document).on('click', '#btnResetTaskFilter', function() {\n");
      out.write("                $('#filterTaskPriority').val('');\n");
      out.write("                $('#filterTaskStatus').val('');\n");
      out.write("                var workOrderId = $('#assign_work_order_id').val();\n");
      out.write("                if(workOrderId) {\n");
