      out.write("        \n");
      out.write("        function loadUserActiveTaskCount(userId) {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/work-order-tasks?action=activeTasks&userId=' + userId,\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if(response && response.success) {\n");
      out.write("                        var count = response.count || 0;\n");
      out.write("                        var tasks = response.tasks || [];\n");
      out.write("                        var userName = $('#assign_user_id option:selected').text();\n");
      out.write("                        \n");
      out.write("                        if(count > 0) {\n");
      out.write("                            var countText = 'Nhân viên <strong>' + userName + '</strong> đang thực hiện <strong>' + count + '</strong> công việc:';\n");
      out.write("                            $('#userTaskCountText').html(countText);\n");
      out.write("                            \n");
      out.write("                            // Hiển thị danh sách công việc\n");
      out.write("                            var tasksList = $('#userActiveTasksItems');\n");
      out.write("                            tasksList.empty();\n");
      out.write("                            tasks.forEach(function(task) {\n");
      out.write("                                var priorityBadge = '';\n");
      out.write("                                if(task.priority === 'high') {\n");
      out.write("                                    priorityBadge = '<span class=\"label label-danger\">Cao</span>';\n");
      out.write("                                } else if(task.priority === 'medium') {\n");
      out.write("                                    priorityBadge = '<span class=\"label label-warning\">Trung bình</span>';\n");
      out.write("                                } else {\n");
      out.write("                                    priorityBadge = '<span class=\"label label-info\">Thấp</span>';\n");
      out.write("                                }\n");
      out.write("                                \n");
      out.write("                                var taskItem = '<li style=\"margin-bottom: 5px;\">' +\n");
      out.write("                                    '<strong>' + (task.taskNumber || 'N/A') + '</strong> - ' +\n");
      out.write("                                    (task.taskDescription || 'Không có mô tả') +\n");
      out.write("                                    ' <small>(' + (task.workOrderNumber || 'N/A') + ': ' + (task.workOrderTitle || 'N/A') + ')</small> ' +\n");
      out.write("                                    priorityBadge +\n");
      out.write("                                    '</li>';\n");
      out.write("                                tasksList.append(taskItem);\n");
      out.write("                            });\n");
      out.write("                            \n");
      out.write("                            $('#userActiveTasksList').show();\n");
      out.write("                            $('#userTaskCountInfo').removeClass('text-danger text-warning text-success').addClass('text-info').show();\n");
      out.write("                        } else {\n");
      out.write("                            var countText = 'Nhân viên <strong>' + userName + '</strong> chưa có công việc nào đang thực hiện';\n");
      out.write("                            $('#userTaskCountText').html(countText);\n");
      out.write("                            $('#userActiveTasksList').hide();\n");
      out.write("                            $('#userTaskCountInfo').removeClass('text-danger text-warning text-info').addClass('text-success').show();\n");
      out.write("                        }\n");
      out.write("                    } else {\n");
      out.write("                        $('#userTaskCountInfo').hide();\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function() {\n");
      out.write("                    console.log('Không thể tải danh sách công việc của nhân viên');\n");
      out.write("                    $('#userTaskCountInfo').hide();\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadTechnicalStaff() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/users?role=technical_staff',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if(response && response.success) {\n");
      out.write("                        technicalStaff = response.data || [];\n");
      out.write("                        populateStaffDropdown();\n");
      out.write("                        // Also populate filter dropdown\n");
      out.write("                        var filterSelect = $('#filterAssignedTo');\n");
      out.write("                        technicalStaff.forEach(function(staff) {\n");
      out.write("                            filterSelect.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\n");
      out.write("                        });\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function() {\n");
      out.write("                    console.log('Không thể tải danh sách nhân viên kỹ thuật');\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function populateStaffDropdown() {\n");
      out.write("            var select = $('#detail_assigned_to');\n");
      out.write("            select.empty();\n");
      out.write("            select.append('<option value=\"\">Chưa phân công</option>');\n");
      out.write("            \n");
      out.write("            technicalStaff.forEach(function(staff) {\n");
      out.write("                select.append('<option value=\"' + staff.id + '\">' + staff.fullName + '</option>');\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadWorkOrders() {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/work-orders?action=list',\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                timeout: 10000,\n");
      out.write("                success: function(response) {\n");
      out.write("                    console.log('Work orders response:', response);\n");
      out.write("                    if(response && response.success) {\n");
      out.write("                        allWorkOrders = response.data || [];\n");
      out.write("                        filteredWorkOrders = allWorkOrders;\n");
      out.write("                        console.log('Loaded ' + allWorkOrders.length + ' work orders');\n");
      out.write("                        // Load assigned users for all work orders (will call renderTable when done)\n");
      out.write("                        loadAllAssignedUsers();\n");
      out.write("                    } else {\n");
      out.write("                        console.error('Failed to load work orders:', response);\n");
      out.write("                        showError('Không thể tải dữ liệu: ' + (response ? response.message : 'Unknown error'));\n");
      out.write("                    }\n");
      out.write("                },\n");
      out.write("                error: function(xhr, status, error) {\n");
      out.write("                    console.error('Error loading work orders:', status, error, xhr);\n");
      out.write("                    showError('Lỗi kết nối máy chủ: ' + error);\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function loadAllAssignedUsers() {\n");
      out.write("            // Reset map\n");
      out.write("            workOrderAssignedUsers = {};\n");
      out.write("            \n");
      out.write("            if(allWorkOrders.length === 0) {\n");
      out.write("                renderTable();\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Initialize all work orders with empty array first\n");
      out.write("            allWorkOrders.forEach(function(workOrder) {\n");
      out.write("                workOrderAssignedUsers[workOrder.id] = [];\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Render table immediately with empty assigned users\n");
      out.write("            renderTable();\n");
      out.write("            \n");
      out.write("            // Load tasks for all work orders\n");
      out.write("            var promises = allWorkOrders.map(function(workOrder) {\n");
      out.write("                return $.ajax({\n");
      out.write("                    url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrder.id,\n");
      out.write("                    type: 'GET',\n");
      out.write("                    dataType: 'json',\n");
      out.write("                    timeout: 5000\n");
      out.write("                }).then(function(response) {\n");
      out.write("                    if(response && response.success && response.data) {\n");
      out.write("                        var assignedUsers = new Set();\n");
      out.write("                        response.data.forEach(function(task) {\n");
      out.write("                            if(task.assignedToName && task.assignedToName.trim() !== '') {\n");
      out.write("                                assignedUsers.add(task.assignedToName);\n");
      out.write("                            }\n");
      out.write("                        });\n");
      out.write("                        workOrderAssignedUsers[workOrder.id] = Array.from(assignedUsers).sort();\n");
      out.write("                    } else {\n");
      out.write("                        workOrderAssignedUsers[workOrder.id] = [];\n");
      out.write("                    }\n");
      out.write("                }).catch(function(error) {\n");
      out.write("                    console.log('Error loading tasks for work order ' + workOrder.id + ':', error);\n");
      out.write("                    workOrderAssignedUsers[workOrder.id] = [];\n");
      out.write("                });\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Wait for all requests to complete, then render table again with updated data\n");
      out.write("            $.when.apply($, promises).always(function() {\n");
      out.write("                renderTable();\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function reloadAssignedUsersForWorkOrder(workOrderId) {\n");
      out.write("            // Reload assigned users for a specific work order\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrderId,\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if(response && response.success && response.data) {\n");
      out.write("                        var assignedUsers = new Set();\n");
      out.write("                        response.data.forEach(function(task) {\n");
      out.write("                            if(task.assignedToName && task.assignedToName.trim() !== '') {\n");
      out.write("                                assignedUsers.add(task.assignedToName);\n");
      out.write("                            }\n");
      out.write("                        });\n");
      out.write("                        workOrderAssignedUsers[workOrderId] = Array.from(assignedUsers).sort();\n");
      out.write("                    } else {\n");
      out.write("                        workOrderAssignedUsers[workOrderId] = [];\n");
      out.write("                    }\n");
      out.write("                    // Re-render table to update the display\n");
      out.write("                    renderTable();\n");
      out.write("                },\n");
      out.write("                error: function() {\n");
      out.write("                    workOrderAssignedUsers[workOrderId] = [];\n");
      out.write("                    renderTable();\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function applyFilters() {\n");
      out.write("            var search = $('#filterSearch').val().toLowerCase();\n");
      out.write("            \n");
      out.write("            filteredWorkOrders = allWorkOrders.filter(function(workOrder) {\n");
      out.write("                var matchSearch = !search || \n");
      out.write("                    (workOrder.workOrderNumber && workOrder.workOrderNumber.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.title && workOrder.title.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.description && workOrder.description.toLowerCase().includes(search)) ||\n");
      out.write("                    (workOrder.customerName && workOrder.customerName.toLowerCase().includes(search));\n");
      out.write("                \n");
      out.write("                return matchSearch;\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            renderTable();\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        function renderTable() {\n");
      out.write("            var tbody = $('#workOrdersTableBody');\n");
      out.write("            tbody.empty();\n");
      out.write("            \n");
      out.write("            if(!filteredWorkOrders || filteredWorkOrders.length === 0) {\n");
      out.write("                tbody.append('<tr><td colspan=\"6\" class=\"text-center\">Không có dữ liệu</td></tr>');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            filteredWorkOrders.forEach(function(workOrder) {\n");
      out.write("                var customerName = workOrder.customerName || 'N/A';\n");
      out.write("                var title = workOrder.title || '';\n");
      out.write("                var estimatedHours = workOrder.estimatedHours ? workOrder.estimatedHours + 'h' : '-';\n");
      out.write("                \n");
      out.write("                // Escape HTML for tooltip\n");
      out.write("                var escapedCustomerName = customerName.replace(/\"/g, '&quot;').replace(/'/g, '&#39;');\n");
      out.write("                var escapedTitle = title.replace(/\"/g, '&quot;').replace(/'/g, '&#39;');\n");
      out.write("                \n");
      out.write("                // Kiểm tra nếu work order đã completed/cancelled/rejected hoặc đã hoàn thành thì ẩn nút Chia việc\n");
      out.write("                var isFinished = workOrder.completionDate && workOrder.status === 'in_progress';\n");
      out.write("                var isWorkOrderClosed = workOrder.status === 'completed' || workOrder.status === 'cancelled' || workOrder.status === 'rejected' || isFinished;\n");
      out.write("                var assignTaskButton = '';\n");
      out.write("                if (!isWorkOrderClosed) {\n");
      out.write("                    assignTaskButton = '<button class=\"btn btn-success btn-assign-task\" data-id=\"' + workOrder.id + '\" title=\"Chia việc\">' +\n");
      out.write("                        '<i class=\"fa fa-tasks\"></i>' +\n");
      out.write("                    '</button>';\n");
      out.write("                } else {\n");
      out.write("                    var disabledTitle = 'Không thể chia việc cho đơn hàng đã đóng';\n");
      out.write("                    if (isFinished) {\n");
      out.write("                        disabledTitle = 'Không thể chia việc cho đơn hàng đã hoàn thành';\n");
      out.write("                    }\n");
      out.write("                    assignTaskButton = '<button class=\"btn btn-success btn-assign-task\" disabled data-id=\"' + workOrder.id + '\" title=\"' + disabledTitle + '\">' +\n");
      out.write("                        '<i class=\"fa fa-tasks\"></i>' +\n");
      out.write("                    '</button>';\n");
      out.write("                }\n");
      out.write("                \n");
      out.write("                var row = '<tr>' +\n");
      out.write("                    '<td><strong>' + (workOrder.workOrderNumber || '#' + workOrder.id) + '</strong></td>' +\n");
      out.write("                    '<td><span class=\"work-order-customer\" title=\"' + escapedCustomerName + '\" data-toggle=\"tooltip\">' + customerName + '</span></td>' +\n");
      out.write("                    '<td><span class=\"work-order-title\" title=\"' + escapedTitle + '\" data-toggle=\"tooltip\">' + title + '</span></td>' +\n");
      out.write("                    '<td class=\"text-center\">' + estimatedHours + '</td>' +\n");
      out.write("                    '<td>' + formatDate(workOrder.createdAt) + '</td>' +\n");
      out.write("                    '<td class=\"work-order-actions\">' +\n");
      out.write("                        '<button class=\"btn btn-info btn-view\" data-id=\"' + workOrder.id + '\" title=\"Xem chi tiết\">' +\n");
      out.write("                            '<i class=\"fa fa-eye\"></i>' +\n");
      out.write("                        '</button>' +\n");
      out.write("                        assignTaskButton +\n");
      out.write("                        '<button class=\"btn btn-warning btn-report\" data-id=\"' + workOrder.id + '\" title=\"Báo cáo\">' +\n");
      out.write("                            '<i class=\"fa fa-file-text-o\"></i>' +\n");
      out.write("                        '</button>' +\n");
      out.write("                    '</td>' +\n");
      out.write("                '</tr>';\n");
      out.write("                tbody.append(row);\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Initialize tooltips for work orders table\n");
      out.write("            $('[data-toggle=\"tooltip\"]').tooltip();\n");
      out.write("            \n");
      out.write("            // Bind view button\n");
      out.write("            $('.btn-view').click(function() {\n");
      out.write("                var id = $(this).data('id');\n");
      out.write("                viewWorkOrderDetail(id);\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // Bind assign task button\n");
      out.write("            $('.btn-assign-task').click(function() {\n");
      out.write("                var id = $(this).data('id');\n");
      out.write("                var workOrder = allWorkOrders.find(function(w) { return w.id == id; });\n");
      out.write("                var isFinished = workOrder && workOrder.completionDate && workOrder.status === 'in_progress';\n");
