      out.write("        // Function to calculate actualHours from tasks\n");
      out.write("        function calculateAndUpdateActualHours(workOrderId) {\n");
      out.write("            $.ajax({\n");
      out.write("                url: ctx + '/api/work-order-tasks?action=list&workOrderId=' + workOrderId,\n");
      out.write("                type: 'GET',\n");
      out.write("                dataType: 'json',\n");
      out.write("                success: function(response) {\n");
      out.write("                    if (response.success && response.data) {\n");
      out.write("                        var tasks = response.data;\n");
      out.write("                        var totalActualHours = 0;\n");
      out.write("                        \n");
      out.write("                        tasks.forEach(function(task) {\n");
      out.write("                            if (task.actualHours && !isNaN(parseFloat(task.actualHours))) {\n");
      out.write("                                totalActualHours += parseFloat(task.actualHours);\n");
      out.write("                            }\n");
      out.write("                        });\n");
      out.write("                        \n");
      out.write("                        // Update actualHours field\n");
