      out.write("        \n");
      out.write("        // Load deadline from support request for work order\n");
      out.write("        function loadWorkOrderDeadline(workOrderId) {\n");
      out.write("            var workOrder = allWorkOrders.find(function(w) { return w.id == workOrderId; });\n");
      out.write("            if (!workOrder) {\n");
      out.write("                // Clear deadline if work order not found\n");
      out.write("                $('#taskStartDate').removeData('workOrderDeadline');\n");
      out.write("                $('#taskDeadline').removeData('workOrderDeadline');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Try to get deadline from support request\n");
      out.write("            // First, try with ticketId if available\n");
      out.write("            if (workOrder.supportRequestId) {\n");
      out.write("                $.ajax({\n");
      out.write("                    url: ctx + '/support-detail',\n");
      out.write("                    type: 'GET',\n");
      out.write("                    data: {\n");
      out.write("                        id: workOrder.supportRequestId\n");
      out.write("                    },\n");
      out.write("                    dataType: 'json',\n");
      out.write("                    success: function(response) {\n");
      out.write("                        if (response && response.success && response.data && response.data.deadline) {\n");
      out.write("                            var deadlineStr = response.data.deadline;\n");
      out.write("                            parseAndStoreDeadline(deadlineStr);\n");
      out.write("                        } else {\n");
      out.write("                            // Try fallback: find by customerId and title\n");
      out.write("                            findDeadlineByCustomerAndTitle(workOrder.customerId, workOrder.title);\n");
      out.write("                        }\n");
      out.write("                    },\n");
      out.write("                    error: function() {\n");
      out.write("                        // Try fallback: find by customerId and title\n");
      out.write("                        findDeadlineByCustomerAndTitle(workOrder.customerId, workOrder.title);\n");
      out.write("                    }\n");
      out.write("                });\n");
      out.write("            } else {\n");
      out.write("                // No ticketId, try to find by customerId and title\n");
      out.write("                findDeadlineByCustomerAndTitle(workOrder.customerId, workOrder.title);\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        // Helper function to find deadline by customerId and title\n");
      out.write("        function findDeadlineByCustomerAndTitle(customerId, title) {\n");
      out.write("            if (!customerId || !title) {\n");
      out.write("                $('#taskStartDate').removeData('workOrderDeadline');\n");
      out.write("                $('#taskDeadline').removeData('workOrderDeadline');\n");
      out.write("                return;\n");
      out.write("            }\n");
      out.write("            \n");
      out.write("            // Use getSupportRequestBySubjectAndCustomer via a workaround\n");
      out.write("            // Since there's no direct API, we'll try to get deadline from work order's ticket ID first\n");
      out.write("            // If that fails, we'll show \"Chưa có deadline\"\n");
      out.write("            console.warn('Cannot load deadline by customer and title - no direct API available');\n");
      out.write("            $('#taskStartDate').removeData('workOrderDeadline');\n");
      out.write("            $('#taskDeadline').removeData('workOrderDeadline');\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        // Helper function to parse and store deadline\n");
      out.write("        function parseAndStoreDeadline(deadlineStr) {\n");
      out.write("            if (deadlineStr && deadlineStr !== '' && deadlineStr !== 'null') {\n");
      out.write("                try {\n");
      out.write("                    var deadlineDate = null;\n");
      out.write("                    // If deadline is in dd/MM/yyyy format\n");
      out.write("                    if (deadlineStr.match(/^\\d{2}\\/\\d{2}\\/\\d{4}$/)) {\n");
      out.write("                        var parts = deadlineStr.split('/');\n");
      out.write("                        deadlineDate = parts[2] + '-' + parts[1] + '-' + parts[0];\n");
      out.write("                    } else if (deadlineStr.match(/^\\d{4}-\\d{2}-\\d{2}$/)) {\n");
      out.write("                        // Already in yyyy-MM-dd format\n");
      out.write("                        deadlineDate = deadlineStr;\n");
      out.write("                    } else {\n");
      out.write("                        // Try to parse as Date and convert to yyyy-MM-dd\n");
      out.write("                        var date = new Date(deadlineStr);\n");
      out.write("                        if (!isNaN(date.getTime())) {\n");
      out.write("                            var year = date.getFullYear();\n");
      out.write("                            var month = String(date.getMonth() + 1).padStart(2, '0');\n");
      out.write("                            var day = String(date.getDate()).padStart(2, '0');\n");
      out.write("                            deadlineDate = year + '-' + month + '-' + day;\n");
      out.write("                        }\n");
      out.write("                    }\n");
      out.write("                    \n");
      out.write("                    if (deadlineDate) {\n");
      out.write("                        // Store deadline in data attribute\n");
      out.write("                        $('#taskStartDate').data('workOrderDeadline', deadlineDate);\n");
      out.write("                        $('#taskDeadline').data('workOrderDeadline', deadlineDate);\n");
      out.write("                        console.log('✓ Deadline loaded and stored:', deadlineDate);\n");
      out.write("                        // Update max attribute for date inputs\n");
      out.write("                        $('#taskStartDate').attr('max', deadlineDate);\n");
      out.write("                        $('#taskDeadline').attr('max', deadlineDate);\n");
      out.write("                    } else {\n");
      out.write("                        console.warn('⚠ Could not parse deadline:', deadlineStr);\n");
      out.write("                    }\n");
      out.write("                } catch (e) {\n");
      out.write("                    console.warn('Error parsing deadline:', e);\n");
      out.write("                    $('#taskStartDate').removeData('workOrderDeadline');\n");
      out.write("                    $('#taskDeadline').removeData('workOrderDeadline');\n");
      out.write("                }\n");
      out.write("            } else {\n");
      out.write("                $('#taskStartDate').removeData('workOrderDeadline');\n");
      out.write("                $('#taskDeadline').removeData('workOrderDeadline');\n");
      out.write("            }\n");
      out.write("        }\n");
